import React from 'react';
import { useParams } from 'react-router-dom';
import { supabase } from '../../config/supabase';

export function DrillDetails() {
  const { id } = useParams();
  const [drill, setDrill] = React.useState(null);
  const [loading, setLoading] = React.useState(true);
  const [expandedSections, setExpandedSections] = React.useState([]);
  const [showVideoPopup, setShowVideoPopup] = React.useState(false);
  const [videoPosition, setVideoPosition] = React.useState({ x: 0, y: 0 });
  const [videoSize, setVideoSize] = React.useState({ width: 400, height: 300 });
  const [isDragging, setIsDragging] = React.useState(false);
  const [isResizing, setIsResizing] = React.useState(false);

  React.useEffect(() => {
    const fetchDrill = async () => {
      try {
        setLoading(true);
        const { data, error } = await supabase
          .from('drills')
          .select('*')
          .eq('id', id)
          .single();

        if (error) throw error;
        setDrill(data);
      } catch (error) {
        console.error('Error fetching drill:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchDrill();
  }, [id]);

  if (loading) {
    return (
      <div className="container mx-auto p-4">
        <div className="text-center py-8">Loading drill details...</div>
      </div>
    );
