import React, { useState } from 'react';
import { Toast } from '../common/Toast';
import DrillItem from './DrillItem';
import ConfirmationModal from '../common/ConfirmationModal';
import { DRILL_FIELDS } from '../../constants/drillFields';
import { useDrillLibrary } from '../../hooks/useDrillLibrary';

export function DrillLibrary() {
  const {
    drills,
    loading,
    error,
    editMode,
    editedDrill,
    toast,
    loadDrills,
    handleStartEdit,
    handleCancelEdit,
    handleFieldEdit,
    handleSaveEdit,
    deleteDrill,
    confirmDelete
  } = useDrillLibrary();

  const [currentDrillToDelete, setCurrentDrillToDelete] = useState(null);

  const handleDelete = (id) => {
    const drill = drills.find(d => d.id === id);
    if (drill) {
      setCurrentDrillToDelete(drill);
    }
  };

  const handleCancelDelete = () => {
    setCurrentDrillToDelete(null);
  };

  return (
    <div className="container mx-auto px-4 py-8">
      {loading ? (
        <div className="text-center py-8">Loading drills...</div>
      ) : drills.length === 0 ? (
        <div className="text-center py-8 text-gray-400">No drills found. Add some drills to get started.</div>
      ) : (
        <div className="space-y-4">
          {drills.map((drill) => (
            <DrillItem
              key={drill.id}
              drill={drill}
              setDrill={(updatedDrill) => {
                loadDrills(); // Refresh the list after update
              }}
              editMode={editMode}
              editedDrill={editedDrill}
              onStartEdit={handleStartEdit}
              onCancelEdit={handleCancelEdit}
              onSaveEdit={handleSaveEdit}
              onDelete={handleDelete}
              onFieldEdit={handleFieldEdit}
              fields={DRILL_FIELDS}
            />
          ))}
        </div>
      )}

      {currentDrillToDelete && (
        <ConfirmationModal
          isOpen={!!currentDrillToDelete}
          onCancel={handleCancelDelete}
          onConfirm={() => {
            confirmDelete();
            setCurrentDrillToDelete(null);
          }}
          title="Delete Drill"
          message={`Are you sure you want to delete the drill "${currentDrillToDelete.name}"?`}
        />
      )}

      {toast.show && (
        <Toast
          type={toast.type}
          message={toast.message}
          onClose={() => setToast(prev => ({ ...prev, show: false }))}
        />
      )}
    </div>
  );
}
