import { useState } from 'react';
import { supabase } from '../config/supabase';
import { useNavigate } from 'react-router-dom';

export function DrillDashboard() {
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [newDrill, setNewDrill] = useState({
    name: '',
    description: '',
    category: '',
    difficulty: '',
    duration: '',
    equipment: '',
    setup: '',
    execution: '',
    variations: '',
    notes: ''
  });
  const [textBlock, setTextBlock] = useState('');
  const navigate = useNavigate();

  async function addDrill() {
    try {
      setLoading(true);
      setError(null);
      
      // Validate required fields
      if (!newDrill.name) {
        throw new Error('Drill name is required');
      }
      
      // Insert the drill into the drills table
      const { data: drillData, error: drillError } = await supabase
        .from('drills')
        .insert([newDrill])
        .select();
      
      if (drillError) throw drillError;
      
      // Get the ID of the newly created drill
      const drillId = drillData[0].id;
      
      // Create an empty instructions entry for this drill
      const { error: instructionsError } = await supabase
        .from('drill_instructions')
        .insert([
          { 
            drill_id: drillId,
            instructions: '',
            steps: [],
            tips: '',
            common_mistakes: '',
            safety_notes: ''
          }
        ]);
      
      if (instructionsError) throw instructionsError;
      
      // Reset form and show success message
      setNewDrill({
        name: '',
        description: '',
        category: '',
        difficulty: '',
        duration: '',
        equipment: '',
        setup: '',
        execution: '',
        variations: '',
        notes: ''
      });
      setTextBlock('');
      
      // Navigate to the drill library
      navigate('/library');
      
    } catch (err) {
      setError('Failed to add drill: ' + err.message);
      console.error('Error adding drill:', err);
    } finally {
      setLoading(false);
    }
  }

  function parseTextBlock() {
    try {
      const lines = textBlock.split('\n');
      const parsedDrill = { ...newDrill };
      
      lines.forEach(line => {
        const [key, ...valueParts] = line.split(':');
        if (key && valueParts.length > 0) {
          const value = valueParts.join(':').trim();
          const normalizedKey = key.trim().toLowerCase().replace(/\s+/g, '_');
          
          if (normalizedKey in parsedDrill) {
            parsedDrill[normalizedKey] = value;
          }
        }
      });
      
      setNewDrill(parsedDrill);
    } catch (err) {
      setError('Failed to parse text block: ' + err.message);
      console.error('Error parsing text block:', err);
    }
  }

  return (
    <div>
      <h2 className="text-2xl font-semibold mb-6 text-orange-400">Add New Drill</h2>
      
      {error && (
        <div className="bg-red-500 text-white p-4 rounded mb-4">
          {error}
        </div>
      )}
      
      <div className="mb-8">
        <h3 className="text-xl font-semibold mb-4 text-blue-400">Paste Full Drill Block</h3>
        <textarea
          className="w-full h-40 p-3 bg-[#2c2c2c] border border-[#3a3a3a] rounded text-white"
          value={textBlock}
          onChange={(e) => setTextBlock(e.target.value)}
          placeholder="Paste drill details here (format: Key: Value)"
        />
        <button
          className="mt-2 bg-blue-600 hover:bg-blue-500 text-white px-4 py-2 rounded"
          onClick={parseTextBlock}
        >
          Parse Block
        </button>
      </div>
      
      <div className="mb-8">
        <h3 className="text-xl font-semibold mb-4 text-blue-400">Drill Details</h3>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div>
            <label className="block mb-2 text-gray-300">Name *</label>
            <input
              type="text"
              className="w-full p-2 bg-[#2c2c2c] border border-[#3a3a3a] rounded text-white"
              value={newDrill.name}
              onChange={(e) => setNewDrill({...newDrill, name: e.target.value})}
              required
            />
          </div>
          <div>
            <label className="block mb-2 text-gray-300">Category</label>
            <input
              type="text"
              className="w-full p-2 bg-[#2c2c2c] border border-[#3a3a3a] rounded text-white"
              value={newDrill.category}
              onChange={(e) => setNewDrill({...newDrill, category: e.target.value})}
            />
          </div>
          <div>
            <label className="block mb-2 text-gray-300">Difficulty</label>
            <input
              type="text"
              className="w-full p-2 bg-[#2c2c2c] border border-[#3a3a3a] rounded text-white"
              value={newDrill.difficulty}
              onChange={(e) => setNewDrill({...newDrill, difficulty: e.target.value})}
            />
          </div>
          <div>
            <label className="block mb-2 text-gray-300">Duration</label>
            <input
              type="text"
              className="w-full p-2 bg-[#2c2c2c] border border-[#3a3a3a] rounded text-white"
              value={newDrill.duration}
              onChange={(e) => setNewDrill({...newDrill, duration: e.target.value})}
            />
          </div>
          <div className="md:col-span-2">
            <label className="block mb-2 text-gray-300">Description</label>
            <textarea
              className="w-full p-2 bg-[#2c2c2c] border border-[#3a3a3a] rounded text-white"
              value={newDrill.description}
              onChange={(e) => setNewDrill({...newDrill, description: e.target.value})}
            />
          </div>
          <div className="md:col-span-2">
            <label className="block mb-2 text-gray-300">Equipment</label>
            <input
              type="text"
              className="w-full p-2 bg-[#2c2c2c] border border-[#3a3a3a] rounded text-white"
              value={newDrill.equipment}
              onChange={(e) => setNewDrill({...newDrill, equipment: e.target.value})}
            />
          </div>
          <div className="md:col-span-2">
            <label className="block mb-2 text-gray-300">Setup</label>
            <textarea
              className="w-full p-2 bg-[#2c2c2c] border border-[#3a3a3a] rounded text-white"
              value={newDrill.setup}
              onChange={(e) => setNewDrill({...newDrill, setup: e.target.value})}
            />
          </div>
          <div className="md:col-span-2">
            <label className="block mb-2 text-gray-300">Execution</label>
            <textarea
              className="w-full p-2 bg-[#2c2c2c] border border-[#3a3a3a] rounded text-white"
              value={newDrill.execution}
              onChange={(e) => setNewDrill({...newDrill, execution: e.target.value})}
            />
          </div>
          <div className="md:col-span-2">
            <label className="block mb-2 text-gray-300">Variations</label>
            <textarea
              className="w-full p-2 bg-[#2c2c2c] border border-[#3a3a3a] rounded text-white"
              value={newDrill.variations}
              onChange={(e) => setNewDrill({...newDrill, variations: e.target.value})}
            />
          </div>
          <div className="md:col-span-2">
            <label className="block mb-2 text-gray-300">Notes</label>
            <textarea
              className="w-full p-2 bg-[#2c2c2c] border border-[#3a3a3a] rounded text-white"
              value={newDrill.notes}
              onChange={(e) => setNewDrill({...newDrill, notes: e.target.value})}
            />
          </div>
        </div>
      </div>
      
      <button
        className="bg-orange-600 hover:bg-orange-500 text-white px-6 py-3 rounded text-lg font-semibold"
        onClick={addDrill}
        disabled={loading}
      >
        {loading ? 'Adding...' : 'Add Drill'}
      </button>
    </div>
  );
}
