import React, { useState, useRef, useEffect } from 'react';
import ReactPlayer from 'react-player';

export default function DrillItem({ 
  drill,
  setDrill,
  onDelete,
  onFieldEdit,
  editMode,
  editedDrill,
  onStartEdit,
  onCancelEdit,
  onSaveEdit,
  fields
}) {
  const [expandedSections, setExpandedSections] = useState(new Set());

  // Preserve expanded state across re-renders
  useEffect(() => {
    if (drill.id) {
      if (expandedSections.size > 0) {
        localStorage.setItem(`drill-${drill.id}-expanded`, JSON.stringify(Array.from(expandedSections)));
      } else {
        localStorage.removeItem(`drill-${drill.id}-expanded`);
      }
    }
  }, [expandedSections, drill.id]);

  // Clear any invalid localStorage data and restore expanded state on mount
  useEffect(() => {
    if (drill.id) {
      try {
        const savedSections = localStorage.getItem(`drill-${drill.id}-expanded`);
        if (savedSections) {
          const parsed = JSON.parse(savedSections);
          if (Array.isArray(parsed)) {
            setExpandedSections(new Set(parsed));
          } else {
            // If not an array, remove invalid data
            localStorage.removeItem(`drill-${drill.id}-expanded`);
          }
        }
      } catch (e) {
        // If parsing fails, remove invalid data
        localStorage.removeItem(`drill-${drill.id}-expanded`);
      }
    }
  }, [drill.id]);

  const toggleSection = (section) => {
    setExpandedSections(prev => {
      const newSections = new Set(prev);
      if (newSections.has(section)) {
        newSections.delete(section);
      } else {
        newSections.add(section);
      }
      return newSections;
    });
  };

  const [isEditingInstructions, setIsEditingInstructions] = useState(false);
  const [editedInstructions, setEditedInstructions] = useState('');
  const [isEditingNotes, setIsEditingNotes] = useState(false);
  const [editedNotes, setEditedNotes] = useState('');
  const [isEditingInfo, setIsEditingInfo] = useState(false);
  const [editedInfo, setEditedInfo] = useState({});
  const [isEditingMechanics, setIsEditingMechanics] = useState(false);
  const [editedMechanics, setEditedMechanics] = useState({});
  const [isEditingVideo, setIsEditingVideo] = useState(false);
  const [editedVideoUrl, setEditedVideoUrl] = useState('');
  const textareaRef = useRef(null);
  const notesTextareaRef = useRef(null);

  // Auto-resize textarea
  const adjustTextareaHeight = (ref) => {
    if (ref.current) {
      ref.current.style.height = 'auto';
      ref.current.style.height = ref.current.scrollHeight + 'px';
    }
  };

  useEffect(() => {
    if (isEditingInstructions) {
      adjustTextareaHeight(textareaRef);
    }
  }, [editedInstructions, isEditingInstructions]);

  useEffect(() => {
    if (isEditingNotes) {
      adjustTextareaHeight(notesTextareaRef);
    }
  }, [editedNotes, isEditingNotes]);

  const handleStartEditInstructions = () => {
    setEditedInstructions(drill.instructions || '');
    setIsEditingInstructions(true);
  };

  const handleStartEditVideo = () => {
    setEditedVideoUrl(drill.video_url || '');
    setIsEditingVideo(true);
  };

  const handleSaveVideo = async () => {
    try {
      await onFieldEdit(drill.id, 'video_url', editedVideoUrl);
      setIsEditingVideo(false);
    } catch (error) {
      console.error('Failed to save video URL:', error);
      // Keep edit mode open if save fails
    }
  };

  const handleCancelEditVideo = () => {
    setIsEditingVideo(false);
    setEditedVideoUrl('');
  };

  const handleStartEditInfo = () => {
    setEditedInfo({
      name: drill.name || '',
      purpose: drill.purpose || '',
      execution_needs: drill.execution_needs || '',
      regression: drill.regression || ''
    });
    setIsEditingInfo(true);
  };

  const handleSaveInfo = async () => {
    try {
      // Save name field separately if it was changed
      if (editedInfo.name !== drill.name) {
        await onFieldEdit(drill.id, 'name', editedInfo.name);
      }

      // Save info fields together
      const infoFields = {
        purpose: editedInfo.purpose,
        execution_needs: editedInfo.execution_needs,
        regression: editedInfo.regression
      };
      await onFieldEdit(drill.id, 'info', infoFields);

      // Update the drill state with new values
      setDrill({ ...drill, ...infoFields });

      setIsEditingInfo(false);
    } catch (error) {
      console.error('Failed to save information:', error);
    }
  };

  const handleCancelEditInfo = () => {
    setIsEditingInfo(false);
    setEditedInfo({});
  };

  const handleInfoFieldChange = (field, value) => {
    setEditedInfo(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleStartEditMechanics = () => {
    setEditedMechanics({
      plane: drill.plane || '',
      muscles: drill.muscles || '',
      joint_actions: drill.joint_actions || '',
      nervous_system: drill.nervous_system || '',
      psych_components: drill.psych_components || '',
      tendons_loaded: drill.tendons_loaded || '',
      influences: drill.influences || ''
    });
    setIsEditingMechanics(true);
  };

  const handleSaveMechanics = async () => {
    try {
      // Save mechanics fields together
      const mechanicsFields = {
        plane: editedMechanics.plane,
        muscles: editedMechanics.muscles,
        joint_actions: editedMechanics.joint_actions,
        nervous_system: editedMechanics.nervous_system,
        psych_components: editedMechanics.psych_components,
        tendons_loaded: editedMechanics.tendons_loaded,
        influences: editedMechanics.influences
      };
      await onFieldEdit(drill.id, 'mechanics', mechanicsFields);

      // Update the drill state with new values
      setDrill({ ...drill, ...mechanicsFields });

      setIsEditingMechanics(false);
    } catch (error) {
      console.error('Failed to save mechanics:', error);
    }
  };

  const handleCancelEditMechanics = () => {
    setIsEditingMechanics(false);
    setEditedMechanics({});
  };

  const handleMechanicsFieldChange = (field, value) => {
    setEditedMechanics(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleCancelEditInstructions = () => {
    setIsEditingInstructions(false);
    setEditedInstructions('');
  };

  const handleSaveInstructions = async () => {
    try {
      // Update instructions using database update functionality
      await onFieldEdit(drill.id, 'instructions', editedInstructions);
      setIsEditingInstructions(false);
    } catch (error) {
      console.error('Error saving instructions:', error);
      // Keep the edit mode open if there's an error
      return;
    }
  };

  const handleStartEditNotes = () => {
    setEditedNotes(drill.notes || '');
    setIsEditingNotes(true);
  };

  const handleCancelEditNotes = () => {
    setIsEditingNotes(false);
    setEditedNotes('');
  };

  const handleSaveNotes = async () => {
    try {
      // Save to database
      await onFieldEdit(drill.id, 'notes', editedNotes);
      // Just close edit mode, keep section expanded
      setIsEditingNotes(false);
    } catch (error) {
      console.error('Error saving notes:', error);
      // Keep the edit mode open if there's an error
      return;
    }
  };

  return (
    <div className="bg-[#2c2c2c] border border-[#3a3a3a] p-4 rounded shadow relative">
      <div className="flex justify-between items-start mb-4">
        <div>
          <h3 className="text-xl font-semibold text-white">{drill.name}</h3>
          <p className="text-gray-400">{drill.category}</p>
        </div>
        {onDelete && (
          <button
            onClick={() => onDelete(drill.id)}
            className="bg-red-600 hover:bg-red-500 text-white px-3 py-1 rounded text-sm"
          >
            Delete Drill
          </button>
        )}
      </div>

      {/* Horizontal buttons */}
      <div className="flex space-x-4 mb-4">
        <button
          onClick={() => toggleSection('info')}
          className={`px-4 py-2 rounded-md ${expandedSections.has('info') ? 'bg-orange-600 text-white' : 'bg-gray-600 hover:bg-gray-500 text-white'}`}
        >
          Information
        </button>
        <button
          onClick={() => toggleSection('mechanics')}
          className={`px-4 py-2 rounded-md ${expandedSections.has('mechanics') ? 'bg-orange-600 text-white' : 'bg-gray-600 hover:bg-gray-500 text-white'}`}
        >
          Mechanics
        </button>
        <button
          onClick={() => toggleSection('instructions')}
          className={`px-4 py-2 rounded-md ${expandedSections.has('instructions') ? 'bg-orange-600 text-white' : 'bg-gray-600 hover:bg-gray-500 text-white'}`}
        >
          Instructions
        </button>
        <button
          onClick={() => toggleSection('video')}
          className={`px-4 py-2 rounded-md ${expandedSections.has('video') ? 'bg-orange-600 text-white' : 'bg-gray-600 hover:bg-gray-500 text-white'}`}
        >
          Video
        </button>
        <button
          onClick={() => toggleSection('notes')}
          className={`px-4 py-2 rounded-md ${expandedSections.has('notes') ? 'bg-orange-600 text-white' : 'bg-gray-600 hover:bg-gray-500 text-white'}`}
        >
          Notes
        </button>
      </div>

      {/* Content sections */}
      <div className="space-y-4">
        {/* Information section */}
        {expandedSections.has('info') && (
          <div className="bg-[#1e1e1e] border border-[#3a3a3a] p-4 rounded-md">
            <div className="flex justify-between items-start mb-4">
              <h4 className="text-gray-400 font-semibold">Information</h4>
              {!isEditingInfo ? (
                <button
                  onClick={handleStartEditInfo}
                  className="bg-blue-600 hover:bg-blue-500 text-white px-3 py-1 rounded text-sm"
                >
                  Edit Information
                </button>
              ) : (
                <div className="space-x-2">
                  <button
                    onClick={handleSaveInfo}
                    className="bg-green-600 hover:bg-green-500 text-white px-3 py-1 rounded text-sm"
                  >
                    Save
                  </button>
                  <button
                    onClick={handleCancelEditInfo}
                    className="bg-gray-600 hover:bg-gray-500 text-white px-3 py-1 rounded text-sm"
                  >
                    Cancel
                  </button>
                </div>
              )}
            </div>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm">
              {[
                { label: 'Name', field: 'name' },
                { label: 'Purpose', field: 'purpose' },
                { label: 'Execution Needs', field: 'execution_needs' },
                { label: 'Regression', field: 'regression' }
              ].map(({ label, field }) => (
                <div key={field}>
                  <span className="text-gray-400">{label}:</span>{' '}
                  {isEditingInfo ? (
                    <input
                      type="text"
                      value={editedInfo[field] || ''}
                      onChange={(e) => handleInfoFieldChange(field, e.target.value)}
                      className="ml-2 w-full bg-[#1e1e1e] border border-[#3a3a3a] rounded px-2 py-1 text-white text-sm"
                    />
                  ) : (
                    <span className="text-white">{drill[field] || 'Not specified'}</span>
                  )}
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Mechanics section */}
        {expandedSections.has('mechanics') && (
          <div className="bg-[#1e1e1e] border border-[#3a3a3a] p-4 rounded-md">
            <div className="flex justify-between items-start mb-4">
              <h4 className="text-gray-400 font-semibold">Mechanics</h4>
              {!isEditingMechanics ? (
                <button
                  onClick={handleStartEditMechanics}
                  className="bg-blue-600 hover:bg-blue-500 text-white px-3 py-1 rounded text-sm"
                >
                  Edit Mechanics
                </button>
              ) : (
                <div className="space-x-2">
                  <button
                    onClick={handleSaveMechanics}
                    className="bg-green-600 hover:bg-green-500 text-white px-3 py-1 rounded text-sm"
                  >
                    Save
                  </button>
                  <button
                    onClick={handleCancelEditMechanics}
                    className="bg-gray-600 hover:bg-gray-500 text-white px-3 py-1 rounded text-sm"
                  >
                    Cancel
                  </button>
                </div>
              )}
            </div>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm">
              {[
                { label: 'Plane', field: 'plane' },
                { label: 'Muscles', field: 'muscles' },
                { label: 'Joint Actions', field: 'joint_actions' },
                { label: 'Nervous System', field: 'nervous_system' },
                { label: 'Psych Components', field: 'psych_components' },
                { label: 'Tendons Loaded', field: 'tendons_loaded' },
                { label: 'Influences', field: 'influences' }
              ].map(({ label, field }) => (
                <div key={field}>
                  <span className="text-gray-400">{label}:</span>{' '}
                  {isEditingMechanics ? (
                    <input
                      type="text"
                      value={editedMechanics[field] || ''}
                      onChange={(e) => handleMechanicsFieldChange(field, e.target.value)}
                      className="ml-2 w-full bg-[#1e1e1e] border border-[#3a3a3a] rounded px-2 py-1 text-white text-sm"
                    />
                  ) : (
                    <span className="text-white">{drill[field] || 'Not specified'}</span>
                  )}
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Video section */}
        {expandedSections.has('video') && (
          <div className="bg-[#1e1e1e] border border-[#3a3a3a] p-4 rounded-md">
            <div className="flex justify-between items-start mb-4">
              <h4 className="text-gray-400 font-semibold">Video</h4>
              {!isEditingVideo ? (
                <button
                  onClick={handleStartEditVideo}
                  className="bg-blue-600 hover:bg-blue-500 text-white px-3 py-1 rounded text-sm"
                >
                  Edit Video URL
                </button>
              ) : (
                <div className="space-x-2">
                  <button
                    onClick={handleSaveVideo}
                    className="bg-green-600 hover:bg-green-500 text-white px-3 py-1 rounded text-sm"
                  >
                    Save
                  </button>
                  <button
                    onClick={handleCancelEditVideo}
                    className="bg-gray-600 hover:bg-gray-500 text-white px-3 py-1 rounded text-sm"
                  >
                    Cancel
                  </button>
                </div>
              )}
            </div>
            {isEditingVideo ? (
              <div className="mb-4">
                <input
                  type="text"
                  value={editedVideoUrl}
                  onChange={(e) => setEditedVideoUrl(e.target.value)}
                  placeholder="Enter video URL (YouTube, Vimeo, etc.)"
                  className="w-full bg-[#1e1e1e] border border-[#3a3a3a] rounded px-3 py-2 text-white text-sm"
                />
              </div>
            ) : drill.video_url ? (
              <div>
                <div className="mb-2 text-white text-sm break-all">{drill.video_url}</div>
                <div className="relative pt-[56.25%] rounded overflow-hidden">
                  <ReactPlayer
                    url={drill.video_url}
                    className="absolute top-0 left-0"
                    width="100%"
                    height="100%"
                    controls
                    config={{
                      youtube: {
                        playerVars: { modestbranding: 1 }
                      }
                    }}
                  />
                </div>
              </div>
            ) : (
              <div className="text-gray-400">No video available.</div>
            )}
          </div>
        )}

        {/* Instructions section */}
        {expandedSections.has('instructions') && (
          <div className="bg-[#1e1e1e] border border-[#3a3a3a] p-4 rounded-md">
            <div className="flex justify-between items-start mb-2">
              <h4 className="text-gray-400 font-semibold">Instructions</h4>
              {!isEditingInstructions ? (
                <button
                  onClick={handleStartEditInstructions}
                  className="bg-blue-600 hover:bg-blue-500 text-white px-3 py-1 rounded text-sm"
                >
                  Edit Instructions
                </button>
              ) : (
                <div className="space-x-2">
                  <button
                    onClick={handleSaveInstructions}
                    className="bg-green-600 hover:bg-green-500 text-white px-3 py-1 rounded text-sm"
                  >
                    Save
                  </button>
                  <button
                    onClick={handleCancelEditInstructions}
                    className="bg-gray-600 hover:bg-gray-500 text-white px-3 py-1 rounded text-sm"
                  >
                    Cancel
                  </button>
                </div>
              )}
            </div>
            {isEditingInstructions ? (
              <textarea
                ref={textareaRef}
                value={editedInstructions}
                onChange={(e) => {
                  setEditedInstructions(e.target.value);
                  adjustTextareaHeight(textareaRef);
                }}
                className="w-full min-h-[100px] bg-[#1e1e1e] border border-[#3a3a3a] rounded p-2 text-white resize-none overflow-hidden"
                placeholder="Enter instructions..."
              />
            ) : (
              <div className="whitespace-pre-wrap text-white">{drill.instructions || 'No instructions provided.'}</div>
            )}
          </div>
        )}

        {/* Notes section */}
        {expandedSections.has('notes') && (
          <div className="bg-[#1e1e1e] border border-[#3a3a3a] p-4 rounded-md">
            <div className="flex justify-between items-start mb-2">
              <h4 className="text-gray-400 font-semibold">Notes</h4>
              {!isEditingNotes ? (
                <button
                  onClick={handleStartEditNotes}
                  className="bg-blue-600 hover:bg-blue-500 text-white px-3 py-1 rounded text-sm"
                >
                  Edit Notes
                </button>
              ) : (
                <div className="space-x-2">
                  <button
                    onClick={handleSaveNotes}
                    className="bg-green-600 hover:bg-green-500 text-white px-3 py-1 rounded text-sm"
                  >
                    Save
                  </button>
                  <button
                    onClick={handleCancelEditNotes}
                    className="bg-gray-600 hover:bg-gray-500 text-white px-3 py-1 rounded text-sm"
                  >
                    Cancel
                  </button>
                </div>
              )}
            </div>
            {isEditingNotes ? (
              <textarea
                ref={notesTextareaRef}
                value={editedNotes}
                onChange={(e) => {
                  setEditedNotes(e.target.value);
                  adjustTextareaHeight(notesTextareaRef);
                }}
                className="w-full min-h-[100px] bg-[#1e1e1e] border border-[#3a3a3a] rounded p-2 text-white resize-none overflow-hidden"
                placeholder="Enter notes..."
              />
            ) : (
              <div className="whitespace-pre-wrap text-white">{drill.notes || 'No notes provided.'}</div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}
