import React from 'react';
import { useParams } from 'react-router-dom';
import { supabase } from '../../config/supabase';

export function DrillDetails() {
  const { id } = useParams();
  const [drill, setDrill] = React.useState(null);
  const [loading, setLoading] = React.useState(true);

  React.useEffect(() => {
    const fetchDrill = async () => {
      try {
        setLoading(true);
        const { data, error } = await supabase
          .from('drills')
          .select('*')
          .eq('id', id)
          .single();

        if (error) throw error;
        setDrill(data);
      } catch (error) {
        console.error('Error fetching drill:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchDrill();
  }, [id]);

  if (loading) {
    return (
      <div className="container mx-auto p-4">
        <div className="text-center py-8">Loading drill details...</div>
      </div>
    );
  }

  return (
    <div className="container mx-auto p-4">
      {/* Sticky Navbar */}
      <div className="sticky top-0 z-10 bg-[#2c2c2c] p-4 shadow-md mb-8">
        <h1 className="text-2xl font-bold text-orange-400">{drill.name}</h1>
      </div>

      {/* Expandable Card Buttons */}
      <div className="grid grid-cols-5 gap-4 mb-8">
        <button className="bg-orange-500 hover:bg-orange-400 text-white px-4 py-2 rounded-lg transition-colors">
          Overview
        </button>
        <button className="bg-blue-500 hover:bg-blue-400 text-white px-4 py-2 rounded-lg transition-colors">
          Objectives
        </button>
        <button className="bg-green-500 hover:bg-green-400 text-white px-4 py-2 rounded-lg transition-colors">
          Resources
        </button>
        <button className="bg-purple-500 hover:bg-purple-400 text-white px-4 py-2 rounded-lg transition-colors">
          Timeline
        </button>
        <button className="bg-indigo-500 hover:bg-indigo-400 text-white px-4 py-2 rounded-lg transition-colors">
          Results
        </button>
      </div>

      {/* Expandable Cards */}
      <div className="space-y-6">
        {/* Overview Card */}
        <div className="bg-[#2c2c2c] rounded-lg p-6 hover:shadow-lg transition-shadow">
          <h3 className="text-xl font-semibold mb-4">Overview</h3>
          <p className="text-gray-300">{drill.overview}</p>
        </div>

        {/* Objectives Card */}
        <div className="bg-[#2c2c2c] rounded-lg p-6 hover:shadow-lg transition-shadow">
          <h3 className="text-xl font-semibold mb-4">Objectives</h3>
          <p className="text-gray-300">{drill.objectives}</p>
        </div>

        {/* Resources Card */}
        <div className="bg-[#2c2c2c] rounded-lg p-6 hover:shadow-lg transition-shadow">
          <h3 className="text-xl font-semibold mb-4">Resources</h3>
          <p className="text-gray-300">{drill.resources}</p>
        </div>

        {/* Timeline Card */}
        <div className="bg-[#2c2c2c] rounded-lg p-6 hover:shadow-lg transition-shadow">
          <h3 className="text-xl font-semibold mb-4">Timeline</h3>
          <p className="text-gray-300">{drill.timeline}</p>
        </div>

        {/* Results Card */}
        <div className="bg-[#2c2c2c] rounded-lg p-6 hover:shadow-lg transition-shadow">
          <h3 className="text-xl font-semibold mb-4">Results</h3>
          <p className="text-gray-300">{drill.results}</p>
        </div>
      </div>
    </div>
  );
}
