import React from 'react';

export default function DrillEditForm({ drill, editedDrill, onFieldEdit }) {
  const fields = [
    { name: 'purpose', label: 'Purpose' },
    { name: 'nervous_system', label: 'Nervous System' },
    { name: 'psych_components', label: 'Psych Components' },
    { name: 'tendons_loaded', label: 'Tendons Loaded' },
    { name: 'influences', label: 'Influences' },
    { name: 'execution_needs', label: 'Execution Needs' },
    { name: 'regression', label: 'Regression' }
  ];

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 gap-2 text-sm">
      {fields.map(({ name, label }) => (
        <div key={name}>
          <span className="text-gray-400">{label}:</span>{' '}
          <input
            type="text"
            value={editedDrill[drill.id]?.[name] || ''}
            onChange={(e) => onFieldEdit(drill.id, name, e.target.value)}
            className="bg-[#1e1e1e] border border-[#3a3a3a] rounded px-2 py-1 text-white w-full mt-1"
          />
        </div>
      ))}
    </div>
  );
}
