import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';
import { supabase } from '../config/supabase';

export function DrillInstructions() {
  const { drillId } = useParams();
  const [drill, setDrill] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [saving, setSaving] = useState(false);
  const [editMode, setEditMode] = useState(false);
  const [editedInstructions, setEditedInstructions] = useState({
    instructions: '',
    instructions_steps: [],
    instructions_tips: '',
    instructions_common_mistakes: '',
    instructions_safety_notes: ''
  });
  const [newInstructions, setNewInstructions] = useState({
    instructions: '',
    instructions_steps: '',
    instructions_tips: '',
    instructions_common_mistakes: '',
    instructions_safety_notes: ''
  });

  useEffect(() => {
    if (drillId) {
      fetchDrill();
    } else {
      setLoading(false);
    }
  }, [drillId]);

  async function fetchDrill() {
    try {
      setLoading(true);
      setError(null);
      
      // Fetch the drill
      const { data: drillData, error: drillError } = await supabase
        .from('drills')
        .select('*')
        .eq('id', drillId)
        .single();
      
      if (drillError) throw drillError;
      setDrill(drillData);
      
      // Set up the instructions data
      const instructionsData = {
        instructions: drillData.instructions || '',
        instructions_steps: drillData.instructions_steps || [],
        instructions_tips: drillData.instructions_tips || '',
        instructions_common_mistakes: drillData.instructions_common_mistakes || '',
        instructions_safety_notes: drillData.instructions_safety_notes || ''
      };
      
      setEditedInstructions(instructionsData);
    } catch (err) {
      setError('Failed to fetch drill: ' + err.message);
      console.error('Error fetching drill:', err);
    } finally {
      setLoading(false);
    }
  }

  async function saveInstructions() {
    try {
      setSaving(true);
      setError(null);
      
      const { error } = await supabase
        .from('drills')
        .update(editedInstructions)
        .eq('id', drillId);
      
      if (error) throw error;
      
      // Update the drill state with the new instructions
      setDrill({
        ...drill,
        ...editedInstructions
      });
      
      setEditMode(false);
    } catch (err) {
      setError('Failed to save instructions: ' + err.message);
      console.error('Error saving instructions:', err);
    } finally {
      setSaving(false);
    }
  }

  async function addNewInstructions() {
    try {
      setSaving(true);
      setError(null);
      
      // Convert steps string to array
      const stepsArray = newInstructions.instructions_steps
        .split('\n')
        .filter(step => step.trim() !== '');
      
      const instructionsToSave = {
        instructions: newInstructions.instructions,
        instructions_steps: stepsArray,
        instructions_tips: newInstructions.instructions_tips,
        instructions_common_mistakes: newInstructions.instructions_common_mistakes,
        instructions_safety_notes: newInstructions.instructions_safety_notes
      };
      
      const { data, error } = await supabase
        .from('drills')
        .update(instructionsToSave)
        .eq('id', drillId)
        .select();
      
      if (error) throw error;
      
      // Update the drill state with the new instructions
      setDrill({
        ...drill,
        ...instructionsToSave
      });
      
      setEditedInstructions(instructionsToSave);
      setNewInstructions({
        instructions: '',
        instructions_steps: '',
        instructions_tips: '',
        instructions_common_mistakes: '',
        instructions_safety_notes: ''
      });
    } catch (err) {
      setError('Failed to add instructions: ' + err.message);
      console.error('Error adding instructions:', err);
    } finally {
      setSaving(false);
    }
  }

  function handleInputChange(e) {
    const { name, value } = e.target;
    setEditedInstructions({
      ...editedInstructions,
      [name]: value
    });
  }

  function handleNewInputChange(e) {
    const { name, value } = e.target;
    setNewInstructions({
      ...newInstructions,
      [name]: value
    });
  }

  function handleStepsChange(e) {
    const steps = e.target.value.split('\n').filter(step => step.trim() !== '');
    setEditedInstructions({
      ...editedInstructions,
      instructions_steps: steps
    });
  }

  return (
    <div>
      <h2 className="text-2xl font-semibold mb-6 text-orange-400">Drill Instructions</h2>
      <div className="mb-6">
        <Link 
          className="bg-blue-600 hover:bg-blue-500 text-white px-4 py-2 rounded"
          to="/instructions"
        >
          Back to Instructions
        </Link>
      </div>

      {loading ? (
        <div className="text-center py-8">Loading drill instructions...</div>
      ) : error ? (
        <div className="bg-red-500 text-white p-4 rounded mb-4">
          {error}
        </div>
      ) : drillId && !drill ? (
        <div className="text-center py-8 text-gray-400">Drill not found.</div>
      ) : drillId && drill ? (
        <div className="bg-[#2c2c2c] border border-[#3a3a3a] p-6 rounded shadow">
          <div className="flex justify-between items-center mb-4">
            <h3 className="text-xl font-bold text-orange-300">{drill.name} Instructions</h3>
            {!editMode && drill.instructions ? (
              <button 
                className="bg-blue-600 hover:bg-blue-500 text-white px-4 py-2 rounded"
                onClick={() => setEditMode(true)}
              >
                Edit Instructions
              </button>
            ) : null}
          </div>
          
          {editMode ? (
            <div className="space-y-4">
              <div>
                <label className="block mb-2 text-gray-300">Instructions</label>
                <textarea
                  name="instructions"
                  className="w-full p-3 bg-[#1e1e1e] border border-[#3a3a3a] rounded text-white h-32"
                  value={editedInstructions.instructions}
                  onChange={handleInputChange}
                />
              </div>
              
              <div>
                <label className="block mb-2 text-gray-300">Steps (one per line)</label>
                <textarea
                  className="w-full p-3 bg-[#1e1e1e] border border-[#3a3a3a] rounded text-white h-40"
                  value={editedInstructions.instructions_steps.join('\n')}
                  onChange={handleStepsChange}
                />
              </div>
              
              <div>
                <label className="block mb-2 text-gray-300">Tips</label>
                <textarea
                  name="instructions_tips"
                  className="w-full p-3 bg-[#1e1e1e] border border-[#3a3a3a] rounded text-white h-24"
                  value={editedInstructions.instructions_tips}
                  onChange={handleInputChange}
                />
              </div>
              
              <div>
                <label className="block mb-2 text-gray-300">Common Mistakes</label>
                <textarea
                  name="instructions_common_mistakes"
                  className="w-full p-3 bg-[#1e1e1e] border border-[#3a3a3a] rounded text-white h-24"
                  value={editedInstructions.instructions_common_mistakes}
                  onChange={handleInputChange}
                />
              </div>
              
              <div>
                <label className="block mb-2 text-gray-300">Safety Notes</label>
                <textarea
                  name="instructions_safety_notes"
                  className="w-full p-3 bg-[#1e1e1e] border border-[#3a3a3a] rounded text-white h-24"
                  value={editedInstructions.instructions_safety_notes}
                  onChange={handleInputChange}
                />
              </div>
              
              <div className="flex space-x-2">
                <button 
                  className="bg-gray-600 hover:bg-gray-500 text-white px-4 py-2 rounded"
                  onClick={() => {
                    setEditedInstructions({
                      instructions: drill.instructions || '',
                      instructions_steps: drill.instructions_steps || [],
                      instructions_tips: drill.instructions_tips || '',
                      instructions_common_mistakes: drill.instructions_common_mistakes || '',
                      instructions_safety_notes: drill.instructions_safety_notes || ''
                    });
                    setEditMode(false);
                  }}
                >
                  Cancel
                </button>
                <button 
                  className="bg-green-600 hover:bg-green-500 text-white px-4 py-2 rounded"
                  onClick={saveInstructions}
                  disabled={saving}
                >
                  {saving ? 'Saving...' : 'Save'}
                </button>
              </div>
            </div>
          ) : drill.instructions ? (
            <div className="space-y-6">
              {drill.instructions ? (
                <div>
                  <h4 className="text-lg font-semibold text-blue-400 mb-2">Instructions</h4>
                  <p className="text-gray-300 whitespace-pre-line">{drill.instructions}</p>
                </div>
              ) : null}
              
              {drill.instructions_steps && drill.instructions_steps.length > 0 ? (
                <div>
                  <h4 className="text-lg font-semibold text-blue-400 mb-2">Steps</h4>
                  <ol className="list-decimal pl-5 space-y-2">
                    {drill.instructions_steps.map((step, index) => (
                      <li key={index} className="text-gray-300">{step}</li>
                    ))}
                  </ol>
                </div>
              ) : null}
              
              {drill.instructions_tips ? (
                <div>
                  <h4 className="text-lg font-semibold text-blue-400 mb-2">Tips</h4>
                  <p className="text-gray-300 whitespace-pre-line">{drill.instructions_tips}</p>
                </div>
              ) : null}
              
              {drill.instructions_common_mistakes ? (
                <div>
                  <h4 className="text-lg font-semibold text-blue-400 mb-2">Common Mistakes</h4>
                  <p className="text-gray-300 whitespace-pre-line">{drill.instructions_common_mistakes}</p>
                </div>
              ) : null}
              
              {drill.instructions_safety_notes ? (
                <div>
                  <h4 className="text-lg font-semibold text-blue-400 mb-2">Safety Notes</h4>
                  <p className="text-gray-300 whitespace-pre-line">{drill.instructions_safety_notes}</p>
                </div>
              ) : null}
            </div>
          ) : (
            <div className="space-y-4">
              <p className="text-gray-400 italic mb-4">No instructions available yet. Please add instructions for this drill.</p>
              
              <div>
                <label className="block mb-2 text-gray-300">Instructions</label>
                <textarea
                  name="instructions"
                  className="w-full p-3 bg-[#1e1e1e] border border-[#3a3a3a] rounded text-white h-32"
                  value={newInstructions.instructions}
                  onChange={handleNewInputChange}
                  placeholder="Enter general instructions for this drill..."
                />
              </div>
              
              <div>
                <label className="block mb-2 text-gray-300">Steps (one per line)</label>
                <textarea
                  name="instructions_steps"
                  className="w-full p-3 bg-[#1e1e1e] border border-[#3a3a3a] rounded text-white h-40"
                  value={newInstructions.instructions_steps}
                  onChange={handleNewInputChange}
                  placeholder="Enter step-by-step instructions (one per line)..."
                />
              </div>
              
              <div>
                <label className="block mb-2 text-gray-300">Tips</label>
                <textarea
                  name="instructions_tips"
                  className="w-full p-3 bg-[#1e1e1e] border border-[#3a3a3a] rounded text-white h-24"
                  value={newInstructions.instructions_tips}
                  onChange={handleNewInputChange}
                  placeholder="Enter helpful tips for this drill..."
                />
              </div>
              
              <div>
                <label className="block mb-2 text-gray-300">Common Mistakes</label>
                <textarea
                  name="instructions_common_mistakes"
                  className="w-full p-3 bg-[#1e1e1e] border border-[#3a3a3a] rounded text-white h-24"
                  value={newInstructions.instructions_common_mistakes}
                  onChange={handleNewInputChange}
                  placeholder="Enter common mistakes to avoid..."
                />
              </div>
              
              <div>
                <label className="block mb-2 text-gray-300">Safety Notes</label>
                <textarea
                  name="instructions_safety_notes"
                  className="w-full p-3 bg-[#1e1e1e] border border-[#3a3a3a] rounded text-white h-24"
                  value={newInstructions.instructions_safety_notes}
                  onChange={handleNewInputChange}
                  placeholder="Enter safety considerations..."
                />
              </div>
              
              <button 
                className="bg-green-600 hover:bg-green-500 text-white px-4 py-2 rounded"
                onClick={addNewInstructions}
                disabled={saving}
              >
                {saving ? 'Saving...' : 'Add Instructions'}
              </button>
            </div>
          )}
        </div>
      ) : (
        <div className="text-center py-8 text-gray-400">
          <p>Select a drill from the Drill Library to view its instructions.</p>
        </div>
      )}
    </div>
  );
}
