import React from 'react';
import { useParams } from 'react-router-dom';
import { supabase } from '../../config/supabase';

export function DrillDetails() {
  const { id } = useParams();
  const [drill, setDrill] = React.useState(null);
  const [loading, setLoading] = React.useState(true);
  const [expandedSections, setExpandedSections] = React.useState([]);

  React.useEffect(() => {
    const fetchDrill = async () => {
      try {
        setLoading(true);
        const { data, error } = await supabase
          .from('drills')
          .select('*')
          .eq('id', id)
          .single();

        if (error) throw error;
        setDrill(data);
      } catch (error) {
        console.error('Error fetching drill:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchDrill();
  }, [id]);

  if (loading) {
    return (
      <div className="container mx-auto p-4">
        <div className="text-center py-8">Loading drill details...</div>
      </div>
    );
  }

  // Button labels (not connected to database)
  const buttonSections = [
    { id: 'information', title: 'Information' },
    { id: 'mechanics', title: 'Mechanics' },
    { id: 'video', title: 'Video' },
    { id: 'instructions', title: 'Instructions' },
    { id: 'notes', title: 'Notes' }
  ];

  // Database content sections
  const contentSections = [
    { 
      id: 'information', 
      content: (
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          {/* Purpose - 3 columns */}
          <div className="col-span-3 row-span-1 bg-zinc-800 rounded-lg p-4 border border-white/10 shadow-sm hover:shadow-md transition-shadow">
            <h4 className="text-lg font-semibold mb-2">Purpose</h4>
            <p className="text-gray-300">{drill.purpose}</p>
          </div>

          {/* Middle row - 3 columns */}
          <div className="col-span-1 row-span-1 bg-zinc-800 rounded-lg p-4 border border-white/10 shadow-sm hover:shadow-md transition-shadow">
            <h4 className="text-lg font-semibold mb-2">Difficulty</h4>
            <ul className="list-disc list-inside text-gray-300 space-y-2">
              {drill.difficulty.split('\n').map((item, index) => (
                <li key={index}>{item}</li>
              ))}
            </ul>
          </div>

          <div className="col-span-1 row-span-1 bg-zinc-800 rounded-lg p-4 border border-white/10 shadow-sm hover:shadow-md transition-shadow">
            <h4 className="text-lg font-semibold mb-2">Skill Demands</h4>
            <ul className="list-disc list-inside text-gray-300 space-y-2">
              {drill.skill_demands.split('\n').map((item, index) => (
                <li key={index}>{item}</li>
              ))}
            </ul>
          </div>

          <div className="col-span-1 row-span-1 bg-zinc-800 rounded-lg p-4 border border-white/10 shadow-sm hover:shadow-md transition-shadow">
            <h4 className="text-lg font-semibold mb-2">Influences</h4>
            <ul className="list-disc list-inside text-gray-300 space-y-2">
              {drill.influences.split('\n').map((item, index) => (
                <li key={index}>{item}</li>
              ))}
            </ul>
          </div>

          {/* Execution Requirements - 3 columns */}
          <div className="col-span-3 row-span-1 bg-zinc-800 rounded-lg p-4 border border-white/10 shadow-sm hover:shadow-md transition-shadow">
            <h4 className="text-lg font-semibold mb-2">Execution Requirements</h4>
            <ul className="list-disc list-inside text-gray-300 space-y-2">
              {drill.execution_requirements.split('\n').map((item, index) => (
                <li key={index}>{item}</li>
              ))}
            </ul>
          </div>
        </div>
      )
    },
    { 
      id: 'mechanics', 
      content: (
        <ul className="list-disc list-inside text-gray-300 space-y-2">
          {drill.mechanics.split('\n').map((item, index) => (
            <li key={index}>{item}</li>
          ))}
        </ul>
      )
    },
    { 
      id: 'video', 
      content: (
        <ul className="list-disc list-inside text-gray-300 space-y-2">
          {drill.video.split('\n').map((item, index) => (
            <li key={index}>{item}</li>
          ))}
        </ul>
      )
    },
    { 
      id: 'instructions', 
      content: (
        <ul className="list-disc list-inside text-gray-300 space-y-2">
          {drill.instructions.split('\n').map((item, index) => (
            <li key={index}>{item}</li>
          ))}
        </ul>
      )
    },
    { 
      id: 'notes', 
      content: (
        <ul className="list-disc list-inside text-gray-300 space-y-2">
          {drill.notes.split('\n').map((item, index) => (
            <li key={index}>{item}</li>
          ))}
        </ul>
      )
    }
  ];

  const toggleSection = (sectionId) => {
    setExpandedSections(prev => 
      prev.includes(sectionId) 
        ? prev.filter(id => id !== sectionId) 
        : [...prev, sectionId]
    );
  };

  return (
    <div className="container mx-auto p-4">
      {/* Main Card */}
      <div className="bg-[#2c2c2c] rounded-lg p-6 hover:shadow-lg transition-shadow border border-white/10">
        {/* Sticky Navbar */}
        <div className="sticky top-0 z-10 bg-[#2c2c2c] p-4 shadow-md mb-8">
          <h1 className="text-2xl font-bold text-orange-400">{drill.name}</h1>
        </div>

        {/* Section Buttons */}
        <div className="grid grid-cols-5 gap-4 mb-8">
          {buttonSections.map((section) => (
            <button
              key={section.id}
              onClick={() => toggleSection(section.id)}
              className={`px-4 py-2 rounded-lg transition-colors ${
                expandedSections.includes(section.id)
                  ? 'bg-orange-500 text-white'
                  : 'bg-gray-700 text-gray-300 hover:bg-gray-600'
              }`}
            >
              {section.title}
            </button>
          ))}
        </div>

        {/* Sections Content */}
        <div className="space-y-4">
          {contentSections.map((section) => (
            expandedSections.includes(section.id) && (
              <div key={section.id} className="bg-zinc-900 rounded-lg p-6 hover:shadow-lg transition-shadow border border-white/10">
                <h3 className="text-xl font-semibold mb-4">
                  {buttonSections.find(b => b.id === section.id)?.title}
                </h3>
                {typeof section.content === 'string' ? (
                  <p className="text-gray-300">{section.content}</p>
                ) : (
                  section.content
                )}
              </div>
            )
          ))}
        </div>
      </div>
    </div>
  );
}
