import React from 'react';
import { useParams } from 'react-router-dom';
import { supabase } from '../../config/supabase';

export function DrillDetails() {
  const { id } = useParams();
  const [drill, setDrill] = React.useState(null);
  const [loading, setLoading] = React.useState(true);
  const [error, setError] = React.useState(null);

  React.useEffect(() => {
    const fetchDrill = async () => {
      try {
        setLoading(true);
        const { data, error } = await supabase
          .from('drills')
          .select('*')
          .eq('id', id)
          .single();

        if (error) throw error;
        setDrill(data);
      } catch (error) {
        console.error('Error fetching drill:', error);
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchDrill();
  }, [id]);

  if (loading) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="text-center py-8">Loading drill details...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="text-center text-red-400">Error loading drill: {error}</div>
      </div>
    );
  }

  if (!drill) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="text-center text-gray-400">Drill not found</div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="bg-[#2c2c2c] rounded-lg p-8">
        <h2 className="text-2xl font-bold text-orange-400 mb-6">{drill.name}</h2>

        <div className="space-y-6">
          {Object.entries(drill).map(([key, value]) => {
            // Skip ID and name since they're already displayed
            if (key === 'id' || key === 'name') return null;

            // Skip null or empty values
            if (!value) return null;

            return (
              <div key={key} className="space-y-2">
                <h3 className="text-lg font-semibold text-orange-400">{key.replace('_', ' ').replace(/\w+/g, (w) => w.charAt(0).toUpperCase() + w.slice(1))}</h3>
                <p className="text-gray-300">{value}</p>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}
