import React, { useState, useRef, useEffect } from 'react';
import DrillEditForm from './DrillEditForm';
import ReactPlayer from 'react-player';

export default function DrillItem({ 
  drill, 
  editMode, 
  editedDrill, 
  onStartEdit, 
  onCancelEdit, 
  onSaveEdit, 
  onDelete,
  onFieldEdit 
}) {
  const [expandedSection, setExpandedSection] = useState(null);
  const [isEditingInstructions, setIsEditingInstructions] = useState(false);
  const [editedInstructions, setEditedInstructions] = useState('');
  const [isEditingNotes, setIsEditingNotes] = useState(false);
  const [editedNotes, setEditedNotes] = useState('');
  const textareaRef = useRef(null);
  const notesTextareaRef = useRef(null);

  // Auto-resize textarea
  const adjustTextareaHeight = (ref) => {
    if (ref.current) {
      ref.current.style.height = 'auto';
      ref.current.style.height = ref.current.scrollHeight + 'px';
    }
  };

  useEffect(() => {
    if (isEditingInstructions) {
      adjustTextareaHeight(textareaRef);
    }
  }, [editedInstructions, isEditingInstructions]);

  useEffect(() => {
    if (isEditingNotes) {
      adjustTextareaHeight(notesTextareaRef);
    }
  }, [editedNotes, isEditingNotes]);

  const handleStartEditInstructions = () => {
    setEditedInstructions(drill.instructions || '');
    setIsEditingInstructions(true);
  };

  const handleCancelEditInstructions = () => {
    setIsEditingInstructions(false);
    setEditedInstructions('');
  };

  const handleSaveInstructions = async () => {
    try {
      // Save to database
      await onFieldEdit(drill.id, 'instructions', editedInstructions);
      // Close edit mode
      setIsEditingInstructions(false);
      // Wait a moment then expand the section
      setTimeout(() => {
        setExpandedSection('instructions');
      }, 300);
    } catch (error) {
      console.error('Error saving instructions:', error);
      // Keep the edit mode open if there's an error
      return;
    }
  };

  const handleStartEditNotes = () => {
    setEditedNotes(drill.notes || '');
    setIsEditingNotes(true);
  };

  const handleCancelEditNotes = () => {
    setIsEditingNotes(false);
    setEditedNotes('');
  };

  const handleSaveNotes = async () => {
    try {
      // Save to database
      await onFieldEdit(drill.id, 'notes', editedNotes);
      // Close edit mode
      setIsEditingNotes(false);
      // Wait a moment then expand the section
      setTimeout(() => {
        setExpandedSection('notes');
      }, 300);
    } catch (error) {
      console.error('Error saving notes:', error);
      // Keep the edit mode open if there's an error
      return;
    }
  };
  return (
    <div className="bg-[#2c2c2c] border border-[#3a3a3a] p-4 rounded shadow relative">
      <div className="absolute top-2 right-2 flex space-x-2">
        {editMode[drill.id] ? (
          <>
            <button 
              className="bg-green-600 hover:bg-green-500 text-white px-2 py-1 rounded"
              onClick={() => onSaveEdit(drill.id)}
            >
              Save
            </button>
            <button 
              className="bg-gray-600 hover:bg-gray-500 text-white px-2 py-1 rounded"
              onClick={() => onCancelEdit(drill.id)}
            >
              Cancel
            </button>
          </>
        ) : (
          <>
            <button 
              className="bg-blue-600 hover:bg-blue-500 text-white px-2 py-1 rounded"
              onClick={() => onStartEdit(drill)}
            >
              Edit
            </button>
            <button 
              className="bg-red-600 hover:bg-red-500 text-white px-2 py-1 rounded"
              onClick={() => onDelete(drill.id)}
            >
              Delete
            </button>
          </>
        )}
      </div>
      <div className="p-4">
        <div className="flex justify-between items-start mb-2">
          <h3 className="text-lg font-semibold">{drill.name}</h3>
        </div>
        {editMode[drill.id] ? (
          <DrillEditForm 
            drill={drill} 
            editedDrill={editedDrill} 
            onFieldEdit={onFieldEdit} 
          />
        ) : (
          <div className="space-y-4 text-sm">
            {/* Two column grid for shorter fields */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div><span className="text-gray-400">Purpose:</span>{' '}{drill.purpose || 'Needs data.'}</div>
              <div><span className="text-gray-400">Plane:</span>{' '}{drill.plane || 'Needs data.'}</div>
              <div><span className="text-gray-400">Muscles:</span>{' '}{drill.muscles || 'Needs data.'}</div>
              <div><span className="text-gray-400">Joint Actions:</span>{' '}{drill.joint_actions || 'Needs data.'}</div>
              <div><span className="text-gray-400">Nervous System:</span>{' '}{drill.nervous_system || 'Needs data.'}</div>
              <div><span className="text-gray-400">Psych Components:</span>{' '}{drill.psych_components || 'Needs data.'}</div>
              <div><span className="text-gray-400">Tendons Loaded:</span>{' '}{drill.tendons_loaded || 'Needs data.'}</div>
              <div><span className="text-gray-400">Influences:</span>{' '}{drill.influences || 'Needs data.'}</div>
              <div><span className="text-gray-400">Execution Needs:</span>{' '}{drill.execution_needs || 'Needs data.'}</div>
              <div><span className="text-gray-400">Regression:</span>{' '}{drill.regression || 'Needs data.'}</div>
            </div>

            {/* Expandable sections */}
            <div className="flex justify-center space-x-4 pt-2">
              <button
                className={`px-4 py-2 rounded ${expandedSection === 'instructions' ? 'bg-orange-600' : 'bg-gray-600 hover:bg-gray-500'}`}
                onClick={() => setExpandedSection(expandedSection === 'instructions' ? null : 'instructions')}
              >
                Instructions
              </button>
              <button
                className={`px-4 py-2 rounded ${expandedSection === 'video' ? 'bg-orange-600' : 'bg-gray-600 hover:bg-gray-500'}`}
                onClick={() => setExpandedSection(expandedSection === 'video' ? null : 'video')}
              >
                Video
              </button>
              <button
                className={`px-4 py-2 rounded ${expandedSection === 'notes' ? 'bg-orange-600' : 'bg-gray-600 hover:bg-gray-500'}`}
                onClick={() => setExpandedSection(expandedSection === 'notes' ? null : 'notes')}
              >
                Notes
              </button>
            </div>

            {/* Expandable content */}
            {expandedSection && (
              <div className="mt-4 p-4 bg-[#1e1e1e] rounded border border-[#3a3a3a]">
                {expandedSection === 'instructions' && (
                  <div>
                    <div className="flex justify-between items-center mb-4">
                      <h4 className="text-gray-400 font-semibold">Instructions</h4>
                      {!isEditingInstructions && (
                        <button
                          className="bg-blue-600 hover:bg-blue-500 text-white px-3 py-1 rounded text-sm"
                          onClick={handleStartEditInstructions}
                        >
                          Edit Instructions
                        </button>
                      )}
                    </div>
                    {isEditingInstructions ? (
                      <div className="space-y-4">
                        <textarea
                          ref={textareaRef}
                          value={editedInstructions}
                          onChange={(e) => setEditedInstructions(e.target.value)}
                          className="w-full min-h-[100px] bg-[#1e1e1e] border border-[#3a3a3a] rounded p-2 text-white resize-none overflow-hidden"
                          placeholder="Enter instructions..."
                        />
                        <div className="flex space-x-2">
                          <button
                            className="bg-green-600 hover:bg-green-500 text-white px-3 py-1 rounded text-sm"
                            onClick={handleSaveInstructions}
                          >
                            Save
                          </button>
                          <button
                            className="bg-gray-600 hover:bg-gray-500 text-white px-3 py-1 rounded text-sm"
                            onClick={handleCancelEditInstructions}
                          >
                            Cancel
                          </button>
                        </div>
                      </div>
                    ) : (
                      <div className="whitespace-pre-wrap">{drill.instructions || 'Needs data.'}</div>
                    )}
                  </div>
                )}
                {expandedSection === 'video' && (
                  <div>
                    <h4 className="text-gray-400 font-semibold mb-2">Video</h4>
                    {drill.video_url ? (
                      <div className="space-y-4">
                        <div className="text-sm text-gray-400 break-all">
                          URL: {drill.video_url}
                        </div>
                        <div className="relative pt-[56.25%] rounded overflow-hidden">
                          <ReactPlayer
                            url={drill.video_url}
                            className="absolute top-0 left-0"
                            width="100%"
                            height="100%"
                            controls
                            config={{
                              youtube: {
                                playerVars: { modestbranding: 1 }
                              }
                            }}
                          />
                        </div>
                      </div>
                    ) : (
                      <div>Needs data.</div>
                    )}
                  </div>
                )}
                {expandedSection === 'notes' && (
                  <div>
                    <div className="flex justify-between items-center mb-4">
                      <h4 className="text-gray-400 font-semibold">Notes</h4>
                      {!isEditingNotes && (
                        <button
                          className="bg-blue-600 hover:bg-blue-500 text-white px-3 py-1 rounded text-sm"
                          onClick={handleStartEditNotes}
                        >
                          Edit Notes
                        </button>
                      )}
                    </div>
                    {isEditingNotes ? (
                      <div className="space-y-4">
                        <textarea
                          ref={notesTextareaRef}
                          value={editedNotes}
                          onChange={(e) => setEditedNotes(e.target.value)}
                          className="w-full min-h-[100px] bg-[#1e1e1e] border border-[#3a3a3a] rounded p-2 text-white resize-none overflow-hidden"
                          placeholder="Enter notes..."
                        />
                        <div className="flex space-x-2">
                          <button
                            className="bg-green-600 hover:bg-green-500 text-white px-3 py-1 rounded text-sm"
                            onClick={handleSaveNotes}
                          >
                            Save
                          </button>
                          <button
                            className="bg-gray-600 hover:bg-gray-500 text-white px-3 py-1 rounded text-sm"
                            onClick={handleCancelEditNotes}
                          >
                            Cancel
                          </button>
                        </div>
                      </div>
                    ) : (
                      <div className="whitespace-pre-wrap">{drill.notes || 'Needs data.'}</div>
                    )}
                  </div>
                )}
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}
