import React from 'react';
import DrillEditForm from './DrillEditForm';

export default function DrillItem({ 
  drill, 
  editMode, 
  editedDrill, 
  onStartEdit, 
  onCancelEdit, 
  onSaveEdit, 
  onDelete,
  onFieldEdit 
}) {
  return (
    <div className="bg-[#2c2c2c] border border-[#3a3a3a] p-4 rounded shadow relative">
      <div className="absolute top-2 right-2 flex space-x-2">
        {editMode[drill.id] ? (
          <>
            <button 
              className="bg-green-600 hover:bg-green-500 text-white px-2 py-1 rounded"
              onClick={() => onSaveEdit(drill.id)}
            >
              Save
            </button>
            <button 
              className="bg-gray-600 hover:bg-gray-500 text-white px-2 py-1 rounded"
              onClick={() => onCancelEdit(drill.id)}
            >
              Cancel
            </button>
          </>
        ) : (
          <>
            <button 
              className="bg-blue-600 hover:bg-blue-500 text-white px-2 py-1 rounded"
              onClick={() => onStartEdit(drill)}
            >
              Edit
            </button>
            <button 
              className="bg-red-600 hover:bg-red-500 text-white px-2 py-1 rounded"
              onClick={() => onDelete(drill.id)}
            >
              Delete
            </button>
          </>
        )}
      </div>
      <div className="p-4">
        <div className="flex justify-between items-start mb-2">
          <h3 className="text-lg font-semibold">{drill.name}</h3>
        </div>
        {editMode[drill.id] ? (
          <DrillEditForm 
            drill={drill} 
            editedDrill={editedDrill} 
            onFieldEdit={onFieldEdit} 
          />
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 gap-2 text-sm">
            <div><span className="text-gray-400">Purpose:</span>{' '}{drill.purpose || 'No current data.'}</div>
            <div><span className="text-gray-400">Nervous System:</span>{' '}{drill.nervous_system || 'No current data.'}</div>
            <div><span className="text-gray-400">Psych Components:</span>{' '}{drill.psych_components || 'No current data.'}</div>
            <div><span className="text-gray-400">Tendons Loaded:</span>{' '}{drill.tendons_loaded || 'No current data.'}</div>
            <div><span className="text-gray-400">Influences:</span>{' '}{drill.influences || 'No current data.'}</div>
            <div><span className="text-gray-400">Execution Needs:</span>{' '}{drill.execution_needs || 'No current data.'}</div>
            <div><span className="text-gray-400">Regression:</span>{' '}{drill.regression || 'No current data.'}</div>
          </div>
        )}
      </div>
    </div>
  );
}
