import { useState, useEffect } from 'react';
import { supabase } from '../config/supabase';
import { Link } from 'react-router-dom';
import { Toast } from './Toast';

export function DrillLibrary() {
  const [drills, setDrills] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [drillToDelete, setDrillToDelete] = useState(null);
  const [successMessage, setSuccessMessage] = useState('');
  const [showToast, setShowToast] = useState(false);
  const [toastType, setToastType] = useState('success');
  const [expandedDrillId, setExpandedDrillId] = useState(null);
  const [instructions, setInstructions] = useState({});
  const [editedInstructions, setEditedInstructions] = useState({});
  const [saving, setSaving] = useState(false);
  const [editMode, setEditMode] = useState({});

  const showErrorToast = (message) => {
    setError(message);
    setSuccessMessage(message);
    setToastType('error');
    setShowToast(true);
  };

  const showSuccessToast = (message) => {
    setSuccessMessage(message);
    setToastType('success');
    setShowToast(true);
  };

  useEffect(() => {
    fetchDrills();
  }, []);

  async function fetchDrills() {
    try {
      setLoading(true);
      setError(null);
      const { data, error } = await supabase.from('drills').select('*');
      if (error) throw error;
      setDrills(data || []);
    } catch (err) {
      showErrorToast('Failed to fetch drills: ' + err.message);
      console.error('Error fetching drills:', err);
    } finally {
      setLoading(false);
    }
  }

  async function deleteDrill(id) {
    // Set the drill to delete, which will trigger the confirmation popup
    setDrillToDelete(drills.find(drill => drill.id === id));
  }

  async function confirmDelete() {
    if (!drillToDelete) return;
    
    try {
      setLoading(true);
      setError(null);
      
      // First, try to delete any associated instructions
      try {
        const { error: instructionsError } = await supabase
          .from('instructions')
          .delete()
          .eq('drill_id', drillToDelete.id);
        
        if (instructionsError) {
          console.error('Error deleting instructions:', instructionsError);
          // Continue with drill deletion even if instructions deletion fails
        }
      } catch (instructionsErr) {
        console.error('Error deleting instructions:', instructionsErr);
        // Continue with drill deletion even if instructions deletion fails
      }
      
      // Then delete the drill
      const { error } = await supabase
        .from('drills')
        .delete()
        .eq('id', drillToDelete.id);
      
      if (error) throw error;
      
      // Update the local state to remove the deleted drill
      setDrills(drills.filter(drill => drill.id !== drillToDelete.id));
      
      // Show success message
      showSuccessToast('Drill deleted successfully');
    } catch (err) {
      showErrorToast('Failed to delete drill: ' + (err.message || 'Unknown error'));
      console.error('Error deleting drill:', err);
    } finally {
      setLoading(false);
      setDrillToDelete(null); // Close the popup after successful deletion
    }
  }

  async function toggleInstructions(drillId) {
    if (expandedDrillId === drillId) {
      setExpandedDrillId(null);
      setEditMode({ ...editMode, [drillId]: false });
      return;
    }
    
    setExpandedDrillId(drillId);
    
    // If we don't have instructions for this drill yet, fetch them
    if (!instructions[drillId]) {
      try {
        setLoading(true);
        
        // Try to fetch instructions
        const { data, error } = await supabase
          .from('instructions')
          .select('*')
          .eq('drill_id', drillId)
          .single();
        
        if (error) {
          if (error.code === 'PGRST116') {
            // Instructions not found, create empty instructions
            try {
              const { data: newInstructions, error: createError } = await supabase
                .from('instructions')
                .insert([
                  {
                    drill_id: drillId,
                    instructions: ''
                  }
                ])
                .select()
                .single();
              
              if (createError) {
                console.error('Error creating empty instructions:', createError);
                // If the table doesn't exist, we'll just show an empty form
                setInstructions(prev => ({ ...prev, [drillId]: {
                  drill_id: drillId,
                  instructions: ''
                }}));
                setEditedInstructions(prev => ({ ...prev, [drillId]: {
                  drill_id: drillId,
                  instructions: ''
                }}));
                // Set edit mode to true for new instructions
                setEditMode({ ...editMode, [drillId]: true });
              } else {
                setInstructions(prev => ({ ...prev, [drillId]: newInstructions }));
                setEditedInstructions(prev => ({ ...prev, [drillId]: newInstructions }));
                // Set edit mode to true for new instructions
                setEditMode({ ...editMode, [drillId]: true });
              }
            } catch (createErr) {
              console.error('Error creating instructions:', createErr);
              // If there's an error creating instructions, we'll just show an empty form
              setInstructions(prev => ({ ...prev, [drillId]: {
                drill_id: drillId,
                instructions: ''
              }}));
              setEditedInstructions(prev => ({ ...prev, [drillId]: {
                drill_id: drillId,
                instructions: ''
              }}));
              // Set edit mode to true for new instructions
              setEditMode({ ...editMode, [drillId]: true });
            }
          } else {
            console.error('Error fetching instructions:', error);
            setInstructions(prev => ({ ...prev, [drillId]: null }));
          }
        } else {
          setInstructions(prev => ({ ...prev, [drillId]: data }));
          setEditedInstructions(prev => ({ ...prev, [drillId]: data }));
          // Set edit mode to false for existing instructions
          setEditMode({ ...editMode, [drillId]: false });
        }
      } catch (err) {
        console.error('Error in toggleInstructions:', err);
        setInstructions(prev => ({ ...prev, [drillId]: null }));
      } finally {
        setLoading(false);
      }
    }
  }

  // Add a new function to handle adding instructions
  function addInstructions(drillId) {
    // Create empty instructions object
    const emptyInstructions = {
      drill_id: drillId,
      instructions: ''
    };
    
    // Set the instructions and edited instructions
    setInstructions(prev => ({ ...prev, [drillId]: emptyInstructions }));
    setEditedInstructions(prev => ({ ...prev, [drillId]: emptyInstructions }));
    
    // Set edit mode to true
    setEditMode({ ...editMode, [drillId]: true });
  }

  async function saveInstructions(drillId) {
    if (!editedInstructions[drillId]) return;
    
    try {
      setSaving(true);
      
      // Log the data we're trying to save
      console.log('Saving instructions for drill:', drillId);
      console.log('Instructions data:', editedInstructions[drillId]);
      
      // First, try to update the instructions
      const { error: updateError } = await supabase
        .from('instructions')
        .update({
          instructions: editedInstructions[drillId].instructions || ''
        })
        .eq('drill_id', drillId);
      
      if (updateError) {
        console.error('Update error:', updateError);
        
        // If update fails, try to insert the instructions
        if (updateError.code === 'PGRST116' || updateError.code === '42P01') {
          // Record not found or table doesn't exist, try to insert
          console.log('Attempting to insert new instructions record');
          
          // First, check if the table exists by trying to select from it
          const { error: tableCheckError } = await supabase
            .from('instructions')
            .select('count')
            .limit(1);
          
          if (tableCheckError && tableCheckError.code === '42P01') {
            console.log('Table does not exist, creating it first');
            
            // Create the table using SQL
            const { error: createTableError } = await supabase.rpc('create_instructions_table');
            
            if (createTableError) {
              console.error('Error creating table:', createTableError);
              
              // If we can't create the table via RPC, try direct insert which might create the table
              console.log('Trying direct insert which might create the table');
            }
          }
          
          // Try to insert the instructions
          const { data: insertData, error: insertError } = await supabase
            .from('instructions')
            .insert([{
              drill_id: drillId,
              instructions: editedInstructions[drillId].instructions || ''
            }])
            .select();
          
          if (insertError) {
            console.error('Insert error:', insertError);
            
            // If insert fails, try to save to the drills table directly
            console.log('Trying to save instructions to the drills table');
            const { error: drillUpdateError } = await supabase
              .from('drills')
              .update({
                instructions: editedInstructions[drillId].instructions || ''
              })
              .eq('id', drillId);
            
            if (drillUpdateError) {
              console.error('Drill update error:', drillUpdateError);
              throw new Error('Failed to save instructions: ' + (drillUpdateError.message || 'Unknown error'));
            }
            
            // Update the instructions state with the edited instructions
            setInstructions(prev => ({ ...prev, [drillId]: editedInstructions[drillId] }));
            showSuccessToast('Instructions saved to drill record');
            return;
          }
          
          // Update the instructions state with the inserted data
          if (insertData && insertData.length > 0) {
            console.log('Successfully inserted instructions:', insertData[0]);
            setInstructions(prev => ({ ...prev, [drillId]: insertData[0] }));
          }
        } else {
          throw new Error('Failed to update instructions: ' + (updateError.message || 'Unknown error'));
        }
      } else {
        // Update the instructions state with the edited instructions
        console.log('Successfully updated instructions');
        setInstructions(prev => ({ ...prev, [drillId]: editedInstructions[drillId] }));
      }
      
      // Show success message
      showSuccessToast('Instructions saved successfully');
    } catch (err) {
      console.error('Error saving instructions:', err);
      showErrorToast('Failed to save instructions: ' + (err.message || 'Unknown error'));
    } finally {
      setSaving(false);
    }
  }

  function handleInputChange(drillId, field, value) {
    setEditedInstructions(prev => ({
      ...prev,
      [drillId]: {
        ...prev[drillId],
        [field]: value
      }
    }));
  }

  return (
    <div>
      <h2 className="text-2xl font-semibold mb-6 text-orange-400">Drill Library</h2>
      
      <Toast 
        message={successMessage} 
        isVisible={showToast} 
        onClose={() => setShowToast(false)} 
        type={toastType} 
      />
      
      {loading ? (
        <div className="text-center py-8">Loading drills...</div>
      ) : drills.length === 0 ? (
        <div className="text-center py-8 text-gray-400">No drills found. Add some drills to get started.</div>
      ) : (
        <div className="space-y-4">
          {drills.map((drill, index) => (
            <div key={index} className="bg-[#2c2c2c] border border-[#3a3a3a] p-4 rounded shadow relative">
              <button 
                className="bg-red-600 hover:bg-red-500 text-white px-2 py-1 rounded absolute top-2 right-2"
                onClick={() => deleteDrill(drill.id)}
              >
                Delete
              </button>
              <div className="p-4">
                <div className="mb-2">
                  <h3 className="text-lg font-semibold text-orange-300">{drill.name}</h3>
                </div>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-2 text-sm">
                  {drill.purpose && (
                    <div>
                      <span className="text-gray-400">Purpose:</span> {drill.purpose}
                    </div>
                  )}
                  {drill.plane && (
                    <div>
                      <span className="text-gray-400">Plane:</span> {drill.plane}
                    </div>
                  )}
                  {drill.muscles && (
                    <div>
                      <span className="text-gray-400">Muscles:</span> {drill.muscles}
                    </div>
                  )}
                  {drill.joint_actions && (
                    <div>
                      <span className="text-gray-400">Joint Actions:</span> {drill.joint_actions}
                    </div>
                  )}
                  {drill.nervous_system && (
                    <div>
                      <span className="text-gray-400">Nervous System:</span> {drill.nervous_system}
                    </div>
                  )}
                  {drill.psych_components && (
                    <div>
                      <span className="text-gray-400">Psych Components:</span> {drill.psych_components}
                    </div>
                  )}
                  {drill.tendons_loaded && (
                    <div>
                      <span className="text-gray-400">Tendons Loaded:</span> {drill.tendons_loaded}
                    </div>
                  )}
                  {drill.influences && (
                    <div>
                      <span className="text-gray-400">Influences:</span> {drill.influences}
                    </div>
                  )}
                  {drill.execution_needs && (
                    <div>
                      <span className="text-gray-400">Execution Needs:</span> {drill.execution_needs}
                    </div>
                  )}
                  {drill.regression && (
                    <div>
                      <span className="text-gray-400">Regression:</span> {drill.regression}
                    </div>
                  )}
                  {drill.notes && (
                    <div className="col-span-2">
                      <span className="text-gray-400">Notes:</span> {drill.notes}
                    </div>
                  )}
                </div>
                
                {/* Instructions Section */}
                <div className="mt-4 pt-4 border-t border-[#3a3a3a]">
                  <div className="flex justify-between items-center">
                    <button
                      onClick={() => toggleInstructions(drill.id)}
                      className="bg-blue-600 hover:bg-blue-500 text-white px-3 py-1 rounded text-sm inline-block"
                    >
                      {expandedDrillId === drill.id ? 'Hide Instructions' : 'Show Instructions'}
                    </button>
                    
                    {expandedDrillId === drill.id && (
                      <button
                        onClick={() => {
                          if (editMode[drill.id]) {
                            // If already in edit mode, cancel editing
                            setEditMode({ ...editMode, [drill.id]: false });
                          } else if (instructions[drill.id]?.instructions) {
                            // If instructions exist, toggle edit mode
                            setEditMode({ ...editMode, [drill.id]: true });
                          } else {
                            // If no instructions, add new ones
                            addInstructions(drill.id);
                          }
                        }}
                        className="bg-blue-600 hover:bg-blue-500 text-white px-3 py-1 rounded text-sm inline-block"
                      >
                        {editMode[drill.id] ? 'Cancel Editing' : (instructions[drill.id]?.instructions ? 'Edit Instructions' : 'Add Instructions')}
                      </button>
                    )}
                  </div>
                  
                  {expandedDrillId === drill.id && (
                    <div className="mt-4">
                      {loading ? (
                        <div className="text-center py-4">Loading instructions...</div>
                      ) : !instructions[drill.id] ? (
                        <div className="text-center py-4 text-gray-400">No instructions available yet.</div>
                      ) : (
                        <div>
                          {!editMode[drill.id] ? (
                            <>
                              {/* View Mode */}
                              <div className="mb-4">
                                <h4 className="text-md font-semibold text-orange-300 mb-2">Instructions</h4>
                                <div className="bg-[#1a1a1a] border border-[#3a3a3a] p-3 rounded text-white whitespace-pre-wrap">
                                  {instructions[drill.id]?.instructions || 'No instructions available.'}
                                </div>
                              </div>
                            </>
                          ) : (
                            <>
                              {/* Edit Mode */}
                              <div className="mb-4">
                                <h4 className="text-md font-semibold text-orange-300 mb-2">Instructions</h4>
                                <textarea
                                  className="w-full bg-[#1a1a1a] border border-[#3a3a3a] p-2 rounded text-white"
                                  rows="6"
                                  value={editedInstructions[drill.id]?.instructions || ''}
                                  onChange={(e) => handleInputChange(drill.id, 'instructions', e.target.value)}
                                  placeholder="Enter instructions for this drill..."
                                />
                              </div>
                              
                              <div className="flex justify-end">
                                <button
                                  onClick={() => {
                                    saveInstructions(drill.id);
                                    setEditMode({ ...editMode, [drill.id]: false });
                                  }}
                                  disabled={saving}
                                  className="bg-green-600 hover:bg-green-500 text-white px-4 py-2 rounded"
                                >
                                  {saving ? 'Saving...' : 'Save Instructions'}
                                </button>
                              </div>
                            </>
                          )}
                        </div>
                      )}
                    </div>
                  )}
                </div>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Confirmation Popup */}
      {drillToDelete && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-[#2c2c2c] p-6 rounded-lg shadow-lg max-w-md w-full">
            <h3 className="text-xl font-bold text-orange-400 mb-4">Confirm Deletion</h3>
            <p className="mb-6">Are you sure you want to delete this drill?</p>
            <div className="flex justify-end space-x-4">
              <button 
                className="bg-gray-600 hover:bg-gray-500 text-white px-4 py-2 rounded"
                onClick={() => setDrillToDelete(null)}
              >
                Cancel
              </button>
              <button 
                className="bg-red-600 hover:bg-red-500 text-white px-4 py-2 rounded"
                onClick={confirmDelete}
              >
                Yes, Delete
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
