import React, { useState, useRef, useEffect } from 'react';
import ReactPlayer from 'react-player';

export default function DrillItem({ 
  drill,
  onDelete,
  onFieldEdit 
}) {
  const [expandedSections, setExpandedSections] = useState(new Set());

  // Preserve expanded state across re-renders
  useEffect(() => {
    if (drill.id && expandedSections.size > 0) {
      localStorage.setItem(`drill-${drill.id}-expanded`, JSON.stringify(Array.from(expandedSections)));
    }
  }, [expandedSections, drill.id]);

  // Restore expanded state on mount
  useEffect(() => {
    if (drill.id) {
      const savedSections = localStorage.getItem(`drill-${drill.id}-expanded`);
      if (savedSections) {
        setExpandedSections(new Set(JSON.parse(savedSections)));
      }
    }
  }, [drill.id]);

  const toggleSection = (section) => {
    setExpandedSections(prev => {
      const newSections = new Set(prev);
      if (newSections.has(section)) {
        newSections.delete(section);
      } else {
        newSections.add(section);
      }
      return newSections;
    });
  };

  const [isEditingInstructions, setIsEditingInstructions] = useState(false);
  const [editedInstructions, setEditedInstructions] = useState('');
  const [isEditingNotes, setIsEditingNotes] = useState(false);
  const [editedNotes, setEditedNotes] = useState('');
  const textareaRef = useRef(null);
  const notesTextareaRef = useRef(null);

  // Auto-resize textarea
  const adjustTextareaHeight = (ref) => {
    if (ref.current) {
      ref.current.style.height = 'auto';
      ref.current.style.height = ref.current.scrollHeight + 'px';
    }
  };

  useEffect(() => {
    if (isEditingInstructions) {
      adjustTextareaHeight(textareaRef);
    }
  }, [editedInstructions, isEditingInstructions]);

  useEffect(() => {
    if (isEditingNotes) {
      adjustTextareaHeight(notesTextareaRef);
    }
  }, [editedNotes, isEditingNotes]);

  const handleStartEditInstructions = () => {
    setEditedInstructions(drill.instructions || '');
    setIsEditingInstructions(true);
  };

  const handleCancelEditInstructions = () => {
    setIsEditingInstructions(false);
    setEditedInstructions('');
  };

  const handleSaveInstructions = async () => {
    try {
      // Save to database
      await onFieldEdit(drill.id, 'instructions', editedInstructions);
      // Just close edit mode, keep section expanded
      setIsEditingInstructions(false);
    } catch (error) {
      console.error('Error saving instructions:', error);
      // Keep the edit mode open if there's an error
      return;
    }
  };

  const handleStartEditNotes = () => {
    setEditedNotes(drill.notes || '');
    setIsEditingNotes(true);
  };

  const handleCancelEditNotes = () => {
    setIsEditingNotes(false);
    setEditedNotes('');
  };

  const handleSaveNotes = async () => {
    try {
      // Save to database
      await onFieldEdit(drill.id, 'notes', editedNotes);
      // Just close edit mode, keep section expanded
      setIsEditingNotes(false);
    } catch (error) {
      console.error('Error saving notes:', error);
      // Keep the edit mode open if there's an error
      return;
    }
  };

  return (
    <div className="bg-white rounded-lg shadow-md p-4">
      <div className="flex justify-between items-start mb-4">
        <div>
          <h3 className="text-xl font-semibold">{drill.name}</h3>
          <p className="text-gray-600">{drill.category}</p>
        </div>
        {onDelete && (
          <button
            onClick={() => onDelete(drill)}
            className="text-red-500 hover:text-red-700"
          >
            Delete
          </button>
        )}
      </div>

      {/* Horizontal buttons */}
      <div className="flex space-x-4 mb-4">
        <button
          onClick={() => toggleSection('info')}
          className={`px-4 py-2 rounded-md ${expandedSections.has('info') ? 'bg-blue-500 text-white' : 'bg-gray-100 hover:bg-gray-200'}`}
        >
          Information
        </button>
        <button
          onClick={() => toggleSection('instructions')}
          className={`px-4 py-2 rounded-md ${expandedSections.has('instructions') ? 'bg-blue-500 text-white' : 'bg-gray-100 hover:bg-gray-200'}`}
        >
          Instructions
        </button>
        <button
          onClick={() => toggleSection('video')}
          className={`px-4 py-2 rounded-md ${expandedSections.has('video') ? 'bg-blue-500 text-white' : 'bg-gray-100 hover:bg-gray-200'}`}
        >
          Video
        </button>
        <button
          onClick={() => toggleSection('notes')}
          className={`px-4 py-2 rounded-md ${expandedSections.has('notes') ? 'bg-blue-500 text-white' : 'bg-gray-100 hover:bg-gray-200'}`}
        >
          Notes
        </button>
      </div>

      {/* Content sections */}
      <div className="space-y-4">
        {/* Information section */}
        {expandedSections.has('info') && (
          <div className="bg-gray-50 p-4 rounded-md">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm">
              <div><span className="font-medium">Purpose:</span> {drill.purpose || 'Not specified'}</div>
              <div><span className="font-medium">Focus:</span> {drill.focus || 'Not specified'}</div>
              <div><span className="font-medium">Duration:</span> {drill.duration || 'Not specified'}</div>
              <div><span className="font-medium">Equipment:</span> {drill.equipment || 'Not specified'}</div>
              <div><span className="font-medium">Players:</span> {drill.players || 'Not specified'}</div>
              <div><span className="font-medium">Age:</span> {drill.age || 'Not specified'}</div>
              <div><span className="font-medium">Difficulty:</span> {drill.difficulty || 'Not specified'}</div>
              <div><span className="font-medium">Category:</span> {drill.category || 'Not specified'}</div>
              <div><span className="font-medium">Joint Actions:</span> {drill.joint_actions || 'Not specified'}</div>
              <div><span className="font-medium">Nervous System:</span> {drill.nervous_system || 'Not specified'}</div>
              <div><span className="font-medium">Psych Components:</span> {drill.psych_components || 'Not specified'}</div>
              <div><span className="font-medium">Execution Needs:</span> {drill.execution_needs || 'Not specified'}</div>
              <div><span className="font-medium">Regression:</span> {drill.regression || 'Not specified'}</div>
            </div>
          </div>
        )}

        {/* Instructions section */}
        {expandedSections.has('instructions') && (
          <div className="bg-gray-50 p-4 rounded-md">
            <div className="flex justify-between items-start mb-2">
              <h4 className="font-medium">Instructions</h4>
              {!isEditingInstructions ? (
                <button
                  onClick={handleStartEditInstructions}
                  className="text-blue-500 hover:text-blue-700"
                >
                  Edit
                </button>
              ) : (
                <div className="space-x-2">
                  <button
                    onClick={handleSaveInstructions}
                    className="text-green-500 hover:text-green-700"
                  >
                    Save
                  </button>
                  <button
                    onClick={handleCancelEditInstructions}
                    className="text-red-500 hover:text-red-700"
                  >
                    Cancel
                  </button>
                </div>
              )}
            </div>
            {isEditingInstructions ? (
              <textarea
                ref={textareaRef}
                value={editedInstructions}
                onChange={(e) => {
                  setEditedInstructions(e.target.value);
                  adjustTextareaHeight(textareaRef);
                }}
                className="w-full p-2 border rounded-md"
                rows="4"
              />
            ) : (
              <div className="whitespace-pre-wrap">{drill.instructions || 'No instructions provided.'}</div>
            )}
          </div>
        )}

        {/* Video section */}
        {expandedSections.has('video') && (
          <div className="bg-gray-50 p-4 rounded-md">
            <h4 className="font-medium mb-2">Video</h4>
            {drill.video_url ? (
              <div>
                <div className="mb-2">{drill.video_url}</div>
                <div className="aspect-w-16 aspect-h-9">
                  <ReactPlayer
                    url={drill.video_url}
                    width="100%"
                    height="100%"
                    controls
                  />
                </div>
              </div>
            ) : (
              <div>No video available.</div>
            )}
          </div>
        )}

        {/* Notes section */}
        {expandedSections.has('notes') && (
          <div className="bg-gray-50 p-4 rounded-md">
            <div className="flex justify-between items-start mb-2">
              <h4 className="font-medium">Notes</h4>
              {!isEditingNotes ? (
                <button
                  onClick={handleStartEditNotes}
                  className="text-blue-500 hover:text-blue-700"
                >
                  Edit
                </button>
              ) : (
                <div className="space-x-2">
                  <button
                    onClick={handleSaveNotes}
                    className="text-green-500 hover:text-green-700"
                  >
                    Save
                  </button>
                  <button
                    onClick={handleCancelEditNotes}
                    className="text-red-500 hover:text-red-700"
                  >
                    Cancel
                  </button>
                </div>
              )}
            </div>
            {isEditingNotes ? (
              <textarea
                ref={notesTextareaRef}
                value={editedNotes}
                onChange={(e) => {
                  setEditedNotes(e.target.value);
                  adjustTextareaHeight(notesTextareaRef);
                }}
                className="w-full p-2 border rounded-md"
                rows="4"
              />
            ) : (
              <div className="whitespace-pre-wrap">{drill.notes || 'No notes provided.'}</div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}
