import React, { useState, useRef, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { supabase } from '../../config/supabase';
import ReactPlayer from 'react-player';
import { DRILL_FIELDS } from '../../constants/drillFields';
import { Toast } from '../common/Toast';
import ConfirmationModal from '../common/ConfirmationModal';

export function DrillDetails() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [drill, setDrill] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [expandedSections, setExpandedSections] = useState(new Set());
  const [editMode, setEditMode] = useState({});
  const [editedDrill, setEditedDrill] = useState({});
  const [toast, setToast] = useState({
    show: false,
    type: 'success',
    message: ''
  });
  const [drillToDelete, setDrillToDelete] = useState(null);

  useEffect(() => {
    const fetchDrill = async () => {
      try {
        setLoading(true);
        const { data, error } = await supabase
          .from('drills')
          .select('*')
          .eq('id', id)
          .single();

        if (error) throw error;
        setDrill(data);

        // Initialize expanded sections from localStorage if available
        if (data.id) {
          try {
            const savedSections = localStorage.getItem(`drill-${data.id}-expanded`);
            if (savedSections) {
              const parsed = JSON.parse(savedSections);
              if (Array.isArray(parsed)) {
                setExpandedSections(new Set(parsed));
              }
            }
          } catch (e) {
            // If parsing fails, remove invalid data
            localStorage.removeItem(`drill-${data.id}-expanded`);
          }
        }
      } catch (error) {
        console.error('Error fetching drill:', error);
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchDrill();
  }, [id]);

  const toggleSection = (section) => {
    setExpandedSections(prev => {
      const newSections = new Set(prev);
      if (newSections.has(section)) {
        newSections.delete(section);
      } else {
        newSections.add(section);
      }
      return newSections;
    });
  };

  const handleFieldEdit = async (field, value) => {
    try {
      const { error } = await supabase
        .from('drills')
        .update({ [field]: value })
        .eq('id', id);

      if (error) throw error;

      // Update local state
      setDrill(prev => ({
        ...prev,
        [field]: value
      }));

      setToast({
        show: true,
        type: 'success',
        message: 'Field updated successfully'
      });
    } catch (error) {
      console.error('Error updating field:', error);
      setToast({
        show: true,
        type: 'error',
        message: 'Failed to update field'
      });
    }
  };

  const handleDelete = async () => {
    try {
      const { error } = await supabase
        .from('drills')
        .delete()
        .eq('id', id);

      if (error) throw error;

      navigate('/library');
    } catch (error) {
      console.error('Error deleting drill:', error);
      setToast({
        show: true,
        type: 'error',
        message: 'Failed to delete drill'
      });
    }
  };

  const handleCopyInstructions = () => {
    const textToCopy = `
Equipment Requirements:
${drill.equipment_requirements ? drill.equipment_requirements.replace(/\.$/, '').split('.').map(sentence => `• ${sentence.trim()}`).join('\n') : 'Not specified'}

Equipment Alternatives:
${drill.equipment_alternatives ? drill.equipment_alternatives.replace(/\.$/, '').split('.').map(sentence => `• ${sentence.trim()}`).join('\n') : 'Not specified'}

Setup:
${drill.setup ? drill.setup.replace(/\.$/, '').split('.').map(sentence => `• ${sentence.trim()}`).join('\n') : 'Not specified'}

Instructions:
${drill.instructions ? drill.instructions.replace(/\.$/, '').split('.').map(sentence => `• ${sentence.trim()}`).join('\n') : 'Not specified'}

Workout Parameters:
• Sets: ${drill.sets || 'Not specified'}
• Reps: ${drill.reps || 'Not specified'}
• Tempo: ${drill.tempo || 'Not specified'}
• Breath: ${drill.breath || 'Not specified'}
• Rest: ${drill.rest || 'Not specified'}

Cues:
${drill.cues ? drill.cues.replace(/\.$/, '').split('.').map(sentence => `• ${sentence.trim()}`).join('\n') : 'Not specified'}

What to Feel:
${drill.what_to_feel ? drill.what_to_feel.replace(/\.$/, '').split('.').map(sentence => `• ${sentence.trim()}`).join('\n') : 'Not specified'}

What to Avoid:
${drill.what_to_avoid ? drill.what_to_avoid.replace(/\.$/, '').split('.').map(sentence => `• ${sentence.trim()}`).join('\n') : 'Not specified'}

Red Flags:
${drill.red_flags ? drill.red_flags.replace(/\.$/, '').split('.').map(sentence => `• ${sentence.trim()}`).join('\n') : 'Not specified'}

Regressions:
${drill.regressions ? drill.regressions.replace(/\.$/, '').split('.').map(sentence => `• ${sentence.trim()}`).join('\n') : 'Not specified'}

Progressions:
${drill.progressions ? drill.progressions.replace(/\.$/, '').split('.').map(sentence => `• ${sentence.trim()}`).join('\n') : 'Not specified'}
`;

    navigator.clipboard.writeText(textToCopy)
      .then(() => {
        setToast({
          show: true,
          type: 'success',
          message: 'Instructions copied to clipboard!\n\nNote: The text is formatted for Google Docs. You can paste this into any document editor to maintain the bullet points and formatting.'
        });
      })
      .catch(err => {
        console.error('Failed to copy text: ', err);
        setToast({
          show: true,
          type: 'error',
          message: 'Failed to copy instructions. Please try again.'
        });
      });
  };

  if (loading) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="text-center py-8">Loading drill details...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="text-center text-red-400">Error loading drill: {error}</div>
      </div>
    );
  }

  if (!drill) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="text-center text-gray-400">Drill not found</div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="bg-[#2c2c2c] rounded-lg p-8">
        <div className="flex justify-between items-center mb-6">
          <h2 className="text-2xl font-bold text-orange-400">{drill.name}</h2>
          <div className="flex space-x-4">
            <button 
              onClick={() => navigate('/library')}
              className="bg-gray-600 hover:bg-gray-500 text-white px-4 py-2 rounded-lg transition-colors"
            >
              Back to Library
            </button>
            <button 
              onClick={() => setDrillToDelete(id)}
              className="bg-red-600 hover:bg-red-500 text-white px-4 py-2 rounded-lg transition-colors"
            >
              Delete Drill
            </button>
          </div>
        </div>

        <div className="space-y-6">
          {Object.entries(drill).map(([key, value]) => {
            // Skip ID and name since they're already displayed
            if (key === 'id' || key === 'name') return null;

            // Skip null or empty values
            if (!value) return null;

            const fieldName = key.replace('_', ' ').replace(/\w+/g, (w) => w.charAt(0).toUpperCase() + w.slice(1));

            return (
              <div key={key} className="space-y-2">
                <div className="flex justify-between items-center">
                  <h3 className="text-lg font-semibold text-orange-400">{fieldName}</h3>
                  <button
                    onClick={() => toggleSection(key)}
                    className="text-orange-400 hover:text-orange-300"
                  >
                    {expandedSections.has(key) ? '▼' : '▶'}
                  </button>
                </div>

                {expandedSections.has(key) && (
                  <div className="space-y-2">
                    {key === 'video_url' ? (
                      <div className="relative aspect-video">
                        <ReactPlayer
                          url={value}
                          controls
                          width="100%"
                          height="100%"
                          className="rounded-lg"
                        />
                      </div>
                    ) : (
                      <div className="text-gray-300 whitespace-pre-wrap">
                        {value}
                      </div>
                    )}

                    {key === 'instructions' && (
                      <button
                        onClick={handleCopyInstructions}
                        className="mt-2 inline-block bg-orange-500 hover:bg-orange-400 text-white px-4 py-2 rounded-lg transition-colors"
                      >
                        Copy Instructions
                      </button>
                    )}
                  </div>
                )}
              </div>
            );
          })}
        </div>
      </div>

      {drillToDelete && (
        <ConfirmationModal
          isOpen={!!drillToDelete}
          onCancel={() => setDrillToDelete(null)}
          onConfirm={handleDelete}
          title="Delete Drill"
          message={`Are you sure you want to delete the drill "${drill.name}"?`}
        />
      )}

      {toast.show && (
        <Toast
          type={toast.type}
          message={toast.message}
          onClose={() => setToast(prev => ({ ...prev, show: false }))}
        />
      )}
    </div>
  );
}
