import React, { useState, useEffect } from 'react';
import ReactPlayer from 'react-player';
import { useParams, useNavigate } from 'react-router-dom';
import { supabase } from '../../config/supabase';

export function DrillDetails() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [drill, setDrill] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [expandedSections, setExpandedSections] = useState(new Set());

  useEffect(() => {
    const fetchDrill = async () => {
      try {
        setLoading(true);
        const { data, error } = await supabase
          .from('drills')
          .select('*')
          .eq('id', id)
          .single();

        if (error) throw error;
        setDrill(data);
      } catch (error) {
        console.error('Error fetching drill:', error);
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchDrill();
  }, [id]);

  const toggleSection = (section) => {
    setExpandedSections(prev => {
      const newSections = new Set(prev);
      if (newSections.has(section)) {
        newSections.delete(section);
      } else {
        newSections.add(section);
      }
      return newSections;
    });
  };

  const handleCopyInstructions = () => {
    // This function is not defined in the provided code, 
    // so I'm leaving it as is. You should implement the logic to copy instructions here.
  };

  if (loading) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="text-center py-8">Loading drill details...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="text-center text-red-400">Error loading drill: {error}</div>
      </div>
    );
  }

  if (!drill) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="text-center text-gray-400">No drill found</div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <nav className="bg-gray-800">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-16">
            <div className="flex items-center">
              <button 
                onClick={() => navigate('/library')}
                className="text-gray-300 hover:text-white px-3 py-2 rounded-md text-sm font-medium"
              >
                Back to Library
              </button>
            </div>
          </div>
        </div>
      </nav>
      <div className="bg-gray-800 rounded-lg p-6 mt-4">
        <div className="flex justify-between items-center mb-6">
          <h2 className="text-2xl font-bold text-orange-400">{drill.name}</h2>
        </div>

        <div className="space-y-6">
          {Object.entries(drill).map(([key, value]) => {
            // Skip ID and name since they're already displayed
            if (key === 'id' || key === 'name') return null;

            // Skip null or empty values
            if (!value) return null;

            const fieldName = key.replace('_', ' ').replace(/\w+/g, (w) => w.charAt(0).toUpperCase() + w.slice(1));

            return (
              <div key={key} className="space-y-2">
                <div className="flex justify-between items-center">
                  <h3 className="text-lg font-semibold text-orange-400">{fieldName}</h3>
                  <button
                    onClick={() => toggleSection(key)}
                    className="text-orange-400 hover:text-orange-300"
                  >
                    {expandedSections.has(key) ? '▼' : '▶'}
                  </button>
                </div>

                {expandedSections.has(key) && (
                  <div className="space-y-2">
                    {key === 'video_url' ? (
                      <div className="relative aspect-video">
                        <ReactPlayer
                          url={value}
                          controls
                          width="100%"
                          height="100%"
                          className="rounded-lg"
                        />
                      </div>
                    ) : (
                      <div className="text-gray-300 whitespace-pre-wrap">
                        {value}
                      </div>
                    )}

                    {key === 'instructions' && (
                      <button
                        onClick={handleCopyInstructions}
                        className="mt-2 inline-block bg-orange-500 hover:bg-orange-400 text-white px-4 py-2 rounded-lg transition-colors"
                      >
                        Copy Instructions
                      </button>
                    )}
                  </div>
                )}
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}
