import React from 'react';
import { useParams } from 'react-router-dom';
import { supabase } from '../../config/supabase';

export function DrillDetails() {
  const { id } = useParams();
  const [drill, setDrill] = React.useState(null);
  const [loading, setLoading] = React.useState(true);
  const [activeSection, setActiveSection] = React.useState(null);

  React.useEffect(() => {
    const fetchDrill = async () => {
      try {
        setLoading(true);
        const { data, error } = await supabase
          .from('drills')
          .select('*')
          .eq('id', id)
          .single();

        if (error) throw error;
        setDrill(data);
      } catch (error) {
        console.error('Error fetching drill:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchDrill();
  }, [id]);

  if (loading) {
    return (
      <div className="container mx-auto p-4">
        <div className="text-center py-8">Loading drill details...</div>
      </div>
    );
  }

  const sections = [
    { id: 'information', title: 'Information', content: drill.information },
    { id: 'mechanics', title: 'Mechanics', content: drill.mechanics },
    { id: 'instructions', title: 'Instructions', content: drill.instructions },
    { id: 'video', title: 'Video', content: drill.video },
    { id: 'notes', title: 'Notes', content: drill.notes }
  ];

  return (
    <div className="container mx-auto p-4">
      {/* Sticky Navbar */}
      <div className="sticky top-0 z-10 bg-[#2c2c2c] p-4 shadow-md mb-8">
        <h1 className="text-2xl font-bold text-orange-400">{drill.name}</h1>
      </div>

      {/* Section Buttons */}
      <div className="grid grid-cols-5 gap-4 mb-8">
        {sections.map((section) => (
          <button
            key={section.id}
            onClick={() => setActiveSection(section.id === activeSection ? null : section.id)}
            className={`px-4 py-2 rounded-lg transition-colors ${
              section.id === activeSection
                ? 'bg-orange-500 text-white'
                : 'bg-gray-700 text-gray-300 hover:bg-gray-600'
            }`}
          >
            {section.title}
          </button>
        ))}
      </div>

      {/* Active Section Content */}
      {activeSection && (
        <div className="bg-[#2c2c2c] rounded-lg p-6 hover:shadow-lg transition-shadow">
          <h3 className="text-xl font-semibold mb-4">
            {sections.find((s) => s.id === activeSection).title}
          </h3>
          <p className="text-gray-300">
            {sections.find((s) => s.id === activeSection).content}
          </p>
        </div>
      )}
    </div>
  );
}
